INSERT INTO `posts` (`pid`, `title`, `slug`, `description`, `author`, `view`, `category_id`, `image`, `status`, `created_at`, `updated_at`) VALUES
(1, 'PSG có tân binh thứ 5 trong hè 2022', 'psg-co-tan-binh-thu-5-trong-he-2022', '<p>PSG đạt thỏa thuận với Lille về việc chi&ecirc;u mộ tiền vệ Renato Sanches trong m&ugrave;a h&egrave; năm nay.</p>\r\n\r\n<p>Theo<em>&nbsp;L&#39;&Eacute;quipe</em>, Lille đồng &yacute; b&aacute;n Renato Sanches cho PSG với gi&aacute; 15 triệu euro. Thương vụ chỉ c&ograve;n v&agrave;i chi tiết nữa l&agrave; ho&agrave;n tất.</p>\r\n\r\n<p>Ban đầu, cầu thủ người Bồ Đ&agrave;o Nha đạt thỏa thuận gia nhập AC Milan, nhưng sự xuất hiện của cố vấn Luis Campos v&agrave; HLV Christophe Galtier ở PSG khiến tiền vệ n&agrave;y thay đổi quyết định.</p>\r\n\r\n<p>Sanches sẽ l&agrave; t&acirc;n binh thứ 5 của PSG trong h&egrave; 2022, sau Vitinha (41,5 triệu euro), Nordi Mukiele (16 triệu euro), Nuno Mendes (40 triệu euro) v&agrave; Hugo Ekitike (hỏi mượn).</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/62bc3bab75c43_r_d_2361_1623.jpg\" /></p>\r\n\r\n<p><em>Le Parisien</em>&nbsp;tiết lộ Sanches gia nhập PSG với bản hợp đồng k&eacute;o d&agrave;i 3 năm. Lille được cho d&ugrave; kh&ocirc;ng muốn l&agrave;m tăng sức mạnh của đối thủ cạnh tranh PSG, tuy nhi&ecirc;n kh&oacute; cưỡng lại lời đề nghị 15 triệu euro cho một năm hợp đồng của Sanches.</p>\r\n\r\n<p>Tại Euro 2016, khi mới 18 tuổi, Sanches g&oacute;p c&ocirc;ng gi&uacute;p Bồ Đ&agrave;o Nha lần đầu đăng quang ở đấu trường ch&acirc;u lục. Bản th&acirc;n tiền vệ n&agrave;y được bầu chọn &quot;Cầu thủ trẻ hay nhất giải&quot;. Trong c&ugrave;ng năm, anh chiến thắng trong cuộc bầu chọn giải thưởng &quot;Golden Boy&quot; (Cậu b&eacute; v&agrave;ng).</p>\r\n\r\n<p>T&agrave;i năng nở rộ gi&uacute;p Sanches được nhiều CLB lớn săn đ&oacute;n. Hợp đồng 5 năm với Bayern Munich được k&yacute; kết. Sự nghiệp Sanches như được trải thảm đỏ, người ta h&igrave;nh dung về tương lai m&agrave;u hồng, s&aacute;ng lạn cho tiền vệ trẻ, t&agrave;i năng bậc nhất thế giới thời điểm đ&oacute;.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, dưới &aacute;p lực lớn, Sanches kh&ocirc;ng đ&aacute;p ứng được kỳ vọng v&agrave; ng&agrave;y c&agrave;ng trượt d&agrave;i. Phi&ecirc;u bạt sang Anh thi đấu cho Swansea rồi lại quay về Bayern, Sanches vẫn thất bại. Chỉ đến khi Lille xuất hiện, cầu thủ n&agrave;y mới hồi sinh. HLV Christophe Galtier đ&oacute;ng vai tr&ograve; quan trọng gi&uacute;p Sanches lấy lại sự tự tin, v&agrave; đỉnh cao l&agrave; chức v&ocirc; địch Ligue 1 m&ugrave;a 2020/21.</p>\r\n\r\n<p>PSG chi&ecirc;u mộ Sanches sau khi đồng &yacute; để Georginio Wijnaldum ra đi. Tiền vệ người H&agrave; Lan được HLV Christophe Galtier mở đường rời PSG ngay trong h&egrave; n&agrave;y. Roma đang tiến gần tới việc ho&agrave;n tất chữ k&yacute; của cựu sao Liverpool.</p>', 'Từ Quốc Minh', 0, 4, '22080390147sss_1.jpg', 'publish', '2022-08-03 01:59:57', '2022-08-03 02:01:47'),
(2, 'Pogba đánh cược để được đá World Cup 2022', 'pogba-danh-cuoc-de-duoc-da-wc-2022', '<p>Tiền vệ người Ph&aacute;p kh&ocirc;ng chọn phương &aacute;n phẫu thuật để c&oacute; thể kịp hồi phục cho kỳ World Cup 2022.</p>\r\n\r\n<p>Tờ&nbsp;<em>La Gazzetta dello Sport</em>&nbsp;x&aacute;c nhận Paul Pogba chọn phương &aacute;n điều trị chấn thương đầu gối trong năm tuần, thay v&igrave; l&ecirc;n b&agrave;n mổ. Quyết định n&agrave;y được đưa ra sau khi Pogba tham khảo &yacute; kiến từ c&aacute;c b&aacute;c sĩ của Juventus v&agrave; cả tuyển Ph&aacute;p.</p>\r\n\r\n<p>Pogba kh&ocirc;ng phẫu thuật bởi phương &aacute;n n&agrave;y khiến anh nghỉ 5 th&aacute;ng v&agrave; lỡ World Cup 2022. Theo&nbsp;<em>The Athletic</em>, việc kh&ocirc;ng phẫu thuật đồng nghĩa Pogba chấp nhận rủi ro. Trung vệ Samuel Umtiti từng l&agrave;m c&aacute;ch n&agrave;y khi d&iacute;nh chấn thương đầu gối v&agrave; sự nghiệp dần đi xuống.</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/1_1.jpg\" style=\"height:506px; width:900px\" /></p>\r\n\r\n<p>Qu&aacute; tr&igrave;nh hồi phục của Pogba sẽ bắt đầu từ việc tập thể dục v&agrave; phục hồi chức năng ở bể bơi. Sau đ&oacute;, tiền vệ Ph&aacute;p sẽ trở lại s&acirc;n cỏ với c&aacute;c b&agrave;i tập c&aacute; nh&acirc;n, trước khi tập luyện c&ugrave;ng to&agrave;n đội.</p>\r\n\r\n<p>Theo lịch tr&igrave;nh, Pogba sẽ nghỉ 6 trận sắp tới của Juventus, bao gồm trận giao hữu với Atletico Madrid v&agrave;o ng&agrave;y 7/8 v&agrave; 5 v&ograve;ng đầu tại Serie A. D&ugrave; vậy, chưa chắc Pogba c&oacute; thể cống hiến 100% cho Juventus khi ưu ti&ecirc;n của anh l&agrave; hạn chế chấn thương để được dự World Cup tr&ecirc;n đất Qatar v&agrave;o cuối năm.</p>\r\n\r\n<p>Ở tuyển Ph&aacute;p, Pogba vẫn l&agrave; cầu thủ quan trọng trong hệ thống chiến thuật của HLV Didier Deschamps. V&ograve;ng chung kết World Cup 2022 khởi tranh ng&agrave;y 21/11 đến 18/12.</p>\r\n\r\n<p>Pogba được chẩn đo&aacute;n l&agrave; bị r&aacute;ch sụn ch&ecirc;m ở đầu gối. &quot;Sau khi bị đau ở đầu gối phải, Paul Pogba được đưa đi chụp X quang. Kết quả cho thấy cầu thủ n&agrave;y d&iacute;nh chấn thương ở phần sụn ch&ecirc;m&quot;, trang chủ Juventus th&ocirc;ng b&aacute;o.</p>\r\n\r\n<p>Pogba trở lại Juventus theo dạng chuyển nhượng tự do ở h&egrave; n&agrave;y. Tiền vệ 29 tuổi lại rời Old Trafford dưới dạng tự do, sau lần gần nhất v&agrave;o năm 2012 khi c&ograve;n l&agrave; một cầu thủ trẻ. Ng&ocirc;i sao sinh năm 1993 nhận 8 triệu euro lương mỗi m&ugrave;a, chưa bao gồm tiền thưởng th&agrave;nh t&iacute;ch trong hợp đồng k&eacute;o d&agrave;i đến th&aacute;ng 6/2026 tại Turin.</p>\r\n\r\n<p>Tiền vệ người Ph&aacute;p được CĐV ch&agrave;o đ&oacute;n nồng nhiệt trong buổi kiểm tra y tế ở Juventus c&aacute;ch đ&acirc;y v&agrave;i tuần. Pogba c&ugrave;ng Angel di Maria chơi ấn tượng trong thắng lợi 2-0 của CLB trước Guadalajara h&ocirc;m 23/7.</p>', 'Từ Quốc Minh', 0, 4, '220803907311_1.jpg', 'publish', '2022-08-03 02:07:31', NULL),
(3, 'Ronaldo, Maguire bị bôi nhọ nhiều nhất mạng xã hội', 'ronaldo-maguire-bi-boi-nho-nhieu-nhat-mxh', '<p>Bộ đ&ocirc;i ng&ocirc;i sao của MU, Cristiano Ronaldo v&agrave; Harry Maguire, phải hứng chịu nhiều chỉ tr&iacute;ch tr&ecirc;n mạng x&atilde; hội trong thời gian qua.</p>\r\n\r\n<p>Nghi&ecirc;n cứu được thực hiện bởi Học viện Alan Turing v&agrave; Ofcom, Cơ quan truyền th&ocirc;ng Anh, trong nửa đầu m&ugrave;a giải 2021/22 cho thấy Ronaldo phải nhận 12.520 lượt b&ocirc;i nhọ tr&ecirc;n mạng x&atilde; hội Twitter. Xếp ngay sau CR7 l&agrave; đồng đội Harry Maguire (8.954 lượt). Thống k&ecirc; của trung vệ MU nhiều hơn gần 6.400 lượt so với cầu thủ tiếp theo trong danh s&aacute;ch, Marcus Rashford.</p>\r\n\r\n<p>Nghi&ecirc;n cứu của Alan Turing v&agrave; Ofcom đưa ra kết luận th&ocirc;ng qua việc ph&acirc;n t&iacute;ch 2,3 triệu b&agrave;i đăng, trong đ&oacute; gần 60.000 lượt b&igrave;nh luận được ph&aacute;t hiện l&agrave; c&oacute; h&agrave;nh vi b&ocirc;i nhọ một c&aacute; nh&acirc;n.</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/ronaldo.jpeg\" style=\"height:506px; width:900px\" /></p>\r\n\r\n<p>Trong top 10, c&oacute; đến 8 cầu thủ thi đấu cho MU ở m&ugrave;a 2021/22. Ngo&agrave;i Ronaldo, Maguire v&agrave; Rashford, danh s&aacute;ch n&agrave;y c&ograve;n bao gồm Bruno Fernandes, Fred, Jesse Lingard, Paul Pogba v&agrave; David De Gea. M&ugrave;a trước, &quot;Quỷ đỏ&quot; chơi tệ khi kết th&uacute;c m&ugrave;a giải ở vị tr&iacute; thứ 6 tại Ngoại hạng Anh, đồng thời mất v&eacute; dự Champions League.</p>\r\n\r\n<p>2 cầu thủ c&ograve;n lại trong top 10 l&agrave; tiền đạo Harry Kane của Tottenham (2.127 lượt) v&agrave; tiền vệ Jack Grealish của Man City (1.538 lượt).</p>\r\n\r\n<p>2 động th&aacute;i tạo ra nhiều d&ograve;ng b&igrave;nh luận ti&ecirc;u cực nhất tr&ecirc;n mạng x&atilde; hội Twitter l&agrave; khi Ronaldo gia nhập MU từ Juventus v&agrave;o h&egrave; 2021, v&agrave; Maguire đăng b&agrave;i xin lỗi sau khi &quot;Quỷ đỏ&quot; chịu thất bại trước Man City ở derby Manchester v&agrave;o th&aacute;ng 11/2021.</p>\r\n\r\n<p>Ronaldo tiếp tục l&agrave; c&aacute;i t&ecirc;n g&acirc;y tranh c&atilde;i khi xuất hiện tin đồn cầu thủ n&agrave;y muốn rời MU ngay trong kỳ chuyển nhượng h&egrave; 2022. Trong khi đ&oacute;, Maguire bị ch&iacute;nh CĐV MU v&agrave; tuyển Anh la &oacute; nhiều th&aacute;ng gần đ&acirc;y v&igrave; phong độ k&eacute;m. HLV Erik ten Hag cho rằng c&aacute;ch duy nhất để Maguire tho&aacute;t khỏi cảnh bị lăng mạ tr&ecirc;n mạng x&atilde; hội l&agrave; &quot;phải thể hiện m&agrave;n tr&igrave;nh diễn tốt tr&ecirc;n s&acirc;n cỏ&quot;.</p>\r\n\r\n<p>Kevin Bakhurst, Gi&aacute;m đốc phụ tr&aacute;ch ph&aacute;t s&oacute;ng nội dung trực tuyến của Ofcom, cho biết: &ldquo;Những nghi&ecirc;n cứu n&agrave;y l&agrave;m s&aacute;ng tỏ mặt tối của b&oacute;ng đ&aacute;. Việc lăng mạ ai đ&oacute; kh&ocirc;ng n&ecirc;n xuất hiện trong l&agrave;ng thể thao cũng như x&atilde; hội. Để ngăn chặn chuyện n&agrave;y cần nỗ lực cả cả tập thể&quot;.</p>', 'Nguyễn Văn A', 0, 4, '22080391026ronaldo.jpeg', 'publish', '2022-08-03 02:10:26', NULL),
(4, 'Ten Hag có động thái cứng rắn với Ronaldo', 'ten-hag-co-dong-thai-cung-ran-voi-ronaldo', '<p>HLV Erik ten Hag được cho l&agrave; kh&ocirc;ng ngần ngại để Cristiano Ronaldo ngồi dự bị ở trận khai m&agrave;n Premier League gặp Brighton v&agrave;o ng&agrave;y 7/8.</p>\r\n\r\n<p>B&aacute;o ch&iacute; Anh khẳng định HLV Ten Hag kh&ocirc;ng sử dụng Ronaldo ở đội h&igrave;nh xuất ph&aacute;t. L&yacute; do l&agrave; si&ecirc;u sao người Bồ Đ&agrave;o Nha chưa đạt thể trạng tốt nhất. CR7 chỉ mới trở lại v&agrave; chơi 45 ph&uacute;t ở trận h&ograve;a Rayo Vallecano h&ocirc;m 31/7. Thay v&igrave; vậy, Ten Hag sẽ đặt niềm tin v&agrave;o Jadon Sancho, Anthony Martial v&agrave; Marcus Rashford. Bộ 3 n&agrave;y chơi hay, phối hợp ăn &yacute; trong c&aacute;c trận đấu tiền m&ugrave;a giải.</p>\r\n\r\n<p><em>Guardian</em>&nbsp;cho rằng vị HLV người H&agrave; Lan kh&ocirc;ng h&agrave;i l&ograve;ng với những động th&aacute;i gần đ&acirc;y của Ronaldo. Việc CR7 đăng d&ograve;ng trạng th&aacute;i sẽ trở lại thi đấu h&ocirc;m 31/7 khiến chiến lược gia người H&agrave; Lan &quot;cảm thấy kh&ocirc;ng thoải m&aacute;i&quot;. Điều n&agrave;y ảnh hưởng đến kế hoạch nh&acirc;n sự của HLV Ten Hag. Ở trận đấu với Vallecano, anh cũng bỏ ra về sớm.</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/ronaldo.jpeg\" style=\"height:506px; width:900px\" /></p>\r\n\r\n<p><em>AS</em>&nbsp;tiết lộ Ronaldo vẫn nung nấu &yacute; định chia tay MU trong kỳ chuyển nhượng h&egrave; 2022. Anh muốn ho&agrave;n tất việc chọn đội b&oacute;ng mới trước ng&agrave;y 7/8. Điều n&agrave;y đồng nghĩa người đại diện Jorge Mendes của CR7 c&ograve;n chưa đến một tuần để dứt điểm tương lai th&acirc;n chủ m&igrave;nh.</p>\r\n\r\n<p>Ronaldo n&oacute;i thẳng với l&atilde;nh đạo MU về việc muốn ra đi. Đại diện Jorge Mendes tổ chức nhiều cuộc gặp mặt giữa &ocirc;ng v&agrave; c&aacute;c đội b&oacute;ng tr&ecirc;n khắp ch&acirc;u &Acirc;u trong v&agrave;i tuần qua bao gồm Barcelona, ​​Atletico Madrid, Chelsea, Bayern Munich, Napoli. Nh&agrave; v&ocirc; địch Euro 2016 muốn tiếp tục chơi b&oacute;ng ở Champions League.</p>\r\n\r\n<p>Trước đ&oacute;, một đội b&oacute;ng k&igrave;nh địch của Man Utd được cho l&agrave; đ&atilde; li&ecirc;n hệ đại diện Mendes cho trường hợp của Ronaldo. Lời đề nghị c&oacute; sẵn tr&ecirc;n b&agrave;n đ&agrave;m ph&aacute;n v&agrave; Ronaldo đang chờ c&acirc;u trả lời từ Man Utd. D&ugrave; vậy, &quot;Quỷ đỏ&quot; từ chối v&agrave; giữ quan điểm kh&ocirc;ng b&aacute;n Ronaldo.</p>\r\n\r\n<p>Ronaldo c&ograve;n 12 th&aacute;ng trong hợp đồng với Man Utd k&egrave;m t&ugrave;y chọn gia hạn th&ecirc;m một năm. Ronaldo ghi 24 b&agrave;n tr&ecirc;n mọi đấu trường m&ugrave;a trước, l&agrave; cầu thủ ghi b&agrave;n h&agrave;ng đầu của CLB nhưng kh&ocirc;ng thể gi&uacute;p &quot;Quỷ đỏ&quot; c&oacute; v&eacute; dự Champions League.</p>', 'Nguyễn Văn A', 1, 4, '22080391159ronaldo.jpeg', 'publish', '2022-08-03 02:11:59', NULL),
(5, 'Chelsea công bố tân binh thứ 4 trong hè 2022', 'chelsea-cong-bo-tan-binh-thu-4-trong-he-2022', '<p>Đ&ecirc;m 2/8 (giờ H&agrave; Nội), đội chủ s&acirc;n Stamford Bridge th&ocirc;ng b&aacute;o chi&ecirc;u mộ th&agrave;nh c&ocirc;ng tiền vệ trẻ Carney Chukwuemeka.</p>\r\n\r\n<p>Chelsea x&aacute;c nhận đạt được thỏa thuận chi&ecirc;u mộ Carney Chukwuemeka từ Aston Villa. Theo k&yacute; giả Fabrizio Romano, tiền vệ 18 tuổi sẽ tiến h&agrave;nh kiểm tra y tế trước khi đặt b&uacute;t k&yacute; v&agrave;o hợp đồng c&oacute; thời hạn 6 năm, k&eacute;o d&agrave;i tới th&aacute;ng 6/2028 với &quot;The Blues&quot;.</p>\r\n\r\n<p>Nguồn tin từ tờ&nbsp;<em>The Athletic</em>&nbsp;tiết lộ tổng gi&aacute; trị của thương vụ n&agrave;y rơi v&agrave;o khoảng 20 triệu bảng bao gồm phụ ph&iacute;. Chukwuemeka l&agrave; t&acirc;n binh thứ 4 của đội chủ s&acirc;n Stamford Bridge trong m&ugrave;a h&egrave; năm nay. Trước đ&oacute;, họ đ&oacute;n th&agrave;nh c&ocirc;ng Raheem Sterling (55 triệu bảng). Kalidou Koulibaly (34 triệu bảng) v&agrave; thủ m&ocirc;n Gabriel Slonina (8 triệu bảng).</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/aaa.jpg\" style=\"height:602px; width:900px\" /></p>\r\n\r\n<p>Chukwuemeka gia nhập l&ograve; đ&agrave;o tạo trẻ Aston Villa năm 2016. Tới th&aacute;ng 5/2021, anh c&oacute; m&agrave;n ra mắt đội một ở trận gặp Tottenham. M&ugrave;a giải vừa qua, cầu thủ sở trường đ&aacute; tiền vệ trung t&acirc;m n&agrave;y c&oacute; 11 lần ra s&acirc;n ở Premier League.</p>\r\n\r\n<p>Tại v&ograve;ng chung kết U19 Euro 2022 diễn ra v&agrave;o m&ugrave;a h&egrave; n&agrave;y, Chukwuemeka chơi nổi bật. T&agrave;i năng sinh năm 2003 ghi b&agrave;n trong trận chung kết, gi&uacute;p U19 Anh đ&aacute;nh bại Israel 3-1 v&agrave; l&ecirc;n ng&ocirc;i v&ocirc; địch.</p>\r\n\r\n<p>Aston Villa coi Chukwuemeka l&agrave; tương lai của đội. Họ nỗ lực gia hạn hợp đồng với Chukwuemeka từ th&aacute;ng 11 năm ngo&aacute;i nhưng kh&ocirc;ng th&agrave;nh. Barcelona, AC Milan v&agrave; nhiều đội b&oacute;ng ở Italy, Đức cũng muốn c&oacute; chữ k&yacute; của Chukwuemeka.</p>\r\n\r\n<p><em>Daily Mail</em>&nbsp;so s&aacute;nh Chukwuemeka với Jude Bellingham, cầu thủ trẻ đang l&agrave; trụ cột của Dortmund. Chukwuemeka chơi hay ở vai tr&ograve; tiền vệ con thoi, đ&ocirc;i khi cũng được bố tr&iacute; ở vị tr&iacute; số 10 nhờ sự s&aacute;ng tạo.</p>\r\n\r\n<p>Cựu cầu thủ Arsenal, Paul Merson đ&aacute;nh gi&aacute; cao Chukwuemeka. &Ocirc;ng nhận định: &quot;Chukwuemeka nhanh nhẹn, chạy chỗ th&ocirc;ng minh v&agrave; c&oacute; khả năng lướt tr&ecirc;n mặt cỏ. Cậu ấy chắc chắn c&oacute; một tương lai tươi s&aacute;ng&quot;.</p>\r\n\r\n<p>Sau Chukwuemeka, Chelsea khả năng đ&oacute;n th&ecirc;m &iacute;t nhất 2 t&acirc;n binh nữa. Những c&aacute;i t&ecirc;n m&agrave; đội b&oacute;ng n&agrave;y nhắm tới l&agrave; trung vệ Wesley Fofana, hậu vệ c&aacute;nh Denzel Dumfries v&agrave; mới nhất l&agrave; Marc Cucurella.</p>\r\n\r\n<p>Theo nhiều nguồn tin, &quot;The Blues&quot; đạt thỏa thuận bước đầu với Fofana v&agrave; Cucurella. 2 thương vụ n&agrave;y ti&ecirc;u tốn của Chelsea kh&ocirc;ng dưới 50 triệu bảng mỗi người.</p>', 'Nguyễn Văn A', 0, 4, '22080391351aaa.jpg', 'publish', '2022-08-03 02:13:51', NULL),
(6, 'Quán cà phê cần sa có thể cứu du lịch Thái Lan', 'quan-ca-phe-can-sa-co-the-cuu-du-lich-thai-lan', '<p>Khi ranh giới giữa việc sử dụng cần sa cho mục đ&iacute;ch y tế hay giải tr&iacute; rất kh&oacute; để ph&acirc;n định, nhiều qu&aacute;n c&agrave; ph&ecirc; kinh doanh chất n&agrave;y đang ch&agrave;o đ&oacute;n h&agrave;ng trăm kh&aacute;ch du lịch mỗi ng&agrave;y.</p>\r\n\r\n<p>Qu&aacute;n c&agrave; ph&ecirc; cần sa RG420 mới mở c&aacute;ch đ&acirc;y 4 ng&agrave;y ở Khao San, khu vực nổi tiếng của Bangkok với kh&aacute;ch du lịch ba l&ocirc;, lu&ocirc;n chật k&iacute;n người.</p>\r\n\r\n<p>Một số cửa h&agrave;ng tương tự mọc l&ecirc;n khắp Bangkok kể từ khi Th&aacute;i Lan hợp ph&aacute;p h&oacute;a việc trồng v&agrave; ti&ecirc;u thụ cần sa v&agrave;o th&aacute;ng 6, v&agrave;i tuần trước khi dỡ bỏ những hạn chế li&ecirc;n quan đến Covid-19 đối với kh&aacute;ch du lịch nước ngo&agrave;i, theo&nbsp;<em>CNN.</em></p>\r\n\r\n<p>Lượng kh&aacute;ch quốc tế giảm c&ograve;n 2 triệu trong nửa đầu năm nay từ gần 40 triệu v&agrave;o năm 2019. Ongard Panyachatiraksa, chủ sở hữu RG420, v&agrave; những người kh&aacute;c coi qu&aacute;n c&agrave; ph&ecirc; của họ l&agrave; trung t&acirc;m trong nỗ lực hồi sinh ng&agrave;nh du lịch - vốn đ&oacute;ng g&oacute;p khoảng 12% v&agrave;o GDP trước khi đại dịch b&ugrave;ng ph&aacute;t.</p>\r\n\r\n<p>Ongard cho biết h&agrave;ng trăm người gh&eacute; qu&aacute;n mỗi ng&agrave;y v&agrave; anh dự định mở th&ecirc;m c&aacute;c cơ sở kh&aacute;c.</p>\r\n\r\n<p>&ldquo;Kh&aacute;ch h&agrave;ng ch&acirc;u &Acirc;u, Nhật Bản, Mỹ đang t&igrave;m kiếm sativa của Th&aacute;i Lan. Cần sa v&agrave; du lịch đ&uacute;ng l&agrave; cặp b&agrave;i tr&ugrave;ng&rdquo;, anh đề cập đến một chủng cần sa khi tr&ograve; chuyện với&nbsp;<em>Reuters.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/ckfinder/userfiles/images/1.jpg\" style=\"height:600px; width:900px\" /></em></p>\r\n\r\n<p>Kh&ocirc;ng phải ai cũng đồng t&igrave;nh.</p>\r\n\r\n<p>Năm 2018, Th&aacute;i Lan trở th&agrave;nh quốc gia Đ&ocirc;ng Nam &Aacute; đầu ti&ecirc;n hợp ph&aacute;p h&oacute;a cần sa để sử dụng trong y tế.</p>\r\n\r\n<p>Luật cần sa mới được ban h&agrave;nh v&agrave;o th&aacute;ng 6 dẫn đến sự b&ugrave;ng nổ trong việc sử dụng chất n&agrave;y để giải tr&iacute; - điều m&agrave; c&aacute;c quan chức ch&iacute;nh phủ cố gắng ngăn cản v&igrave; lo ngại về ảnh hưởng ti&ecirc;u cực đến sức khỏe v&agrave; năng suất l&agrave;m việc li&ecirc;n quan đến sử dụng cần sa bừa b&atilde;i.</p>\r\n\r\n<p>&ldquo;Luật kh&ocirc;ng bao gồm sử dụng cần sa để giải tr&iacute;. Do đ&oacute;, việc d&ugrave;ng chất n&agrave;y để x&uacute;c tiến du lịch tập trung v&agrave;o kh&iacute;a cạnh y tế&rdquo;, Siripakorn Cheawsamoot, Ph&oacute; thống đốc cơ quan du lịch quốc gia, cho biết.</p>\r\n\r\n<p>C&aacute;ch giải th&iacute;ch ch&iacute;nh s&aacute;ch mới g&acirc;y ra một số nhầm lẫn. Sau đ&oacute;, c&aacute;c nh&agrave; chức tr&aacute;ch phải ban h&agrave;nh quy định chắp v&aacute; như cấm h&uacute;t cần sa nơi c&ocirc;ng cộng v&agrave; b&aacute;n cho người dưới 20 tuổi.</p>\r\n\r\n<p>Một ủy ban quốc hội đang tranh luận về dự luật điều chỉnh việc sử dụng cần sa dự kiến ho&agrave;n th&agrave;nh v&agrave;o th&aacute;ng 9. Điều n&agrave;y c&oacute; thể ảnh hưởng đến c&aacute;c qu&aacute;n c&agrave; ph&ecirc; cần sa.</p>\r\n\r\n<p>Akira Wongwan, nh&agrave; kinh doanh cần sa trong lĩnh vực y tế v&agrave; cố vấn của ủy ban, cho biết c&ocirc; mong muốn việc sử dụng để giải tr&iacute; phải tu&acirc;n theo luật ph&acirc;n v&ugrave;ng.</p>\r\n\r\n<p>Trong khi đ&oacute;, giữa căn ph&ograve;ng đ&ocirc;ng đ&uacute;c ở RG420, Briton Malik Khan (26 tuổi) vừa r&iacute;t xong một hơi cần sa.</p>\r\n\r\n<p>&ldquo;Đất nước n&agrave;y thật tuyệt v&agrave; c&oacute; qu&aacute; nhiều điều để l&agrave;m ở đ&acirc;y. Cần sa khiến lựa chọn trở n&ecirc;n độc đ&aacute;o&rdquo;, anh n&oacute;i.</p>', 'Nguyễn Văn A', 0, 1, '220803915221.jpg', 'publish', '2022-08-03 02:15:22', NULL),
(7, 'Người trẻ đầu tư chơi xe cổ', 'nguoi-tre-dau-tu-choi-xe-co', '<p>Tấn Sang chi 120 triệu đồng mua 3 chiếc xe m&aacute;y cổ. Anh c&oacute; th&ecirc;m một chiếc xe th&ocirc;ng thường để đi đường xa hoặc sử dụng khi mưa, ngập.</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/pexels_pixabay_221299.jpg\" style=\"height:1067px; width:1600px\" /></p>\r\n\r\n<p>Tấn Sang (20 tuổi, quận 12, TP.HCM) chi 60 triệu đồng mua chiếc Vespa Sprint 090 phi&ecirc;n bản năm 1969. Đ&acirc;y l&agrave; chiếc xe m&aacute;y cổ đầu ti&ecirc;n m&agrave; anh sở hữu, kh&ocirc;ng qu&aacute; kh&oacute; t&igrave;m nhưng gi&aacute; trị tương đối cao.</p>\r\n\r\n<p>Sau đ&oacute;, anh c&ograve;n mua th&ecirc;m Honda Cub 1986 v&agrave; Honda Cub &quot;c&aacute;nh &eacute;n&quot; 1978 với tổng số tiền hơn 60 triệu đồng.</p>\r\n\r\n<p>Bắt đầu th&uacute; chơi xe cổ từ năm 2020, Sang kh&ocirc;ng tiếc tiền cho sở th&iacute;ch của m&igrave;nh. Chi ph&iacute; mua xe được anh t&iacute;ch c&oacute;p từ tiền l&agrave;m th&ecirc;m hoặc tiết kiệm nhiều năm.</p>\r\n\r\n<p>&quot;Tiền mua xe th&ocirc;i l&agrave; chưa đủ, t&ocirc;i c&ograve;n chi hơn 15 triệu đồng để t&acirc;n trang, sơn sửa v&agrave; lắp r&aacute;p lại bộ m&aacute;y của chiếc Vespa. Mua phụ t&ugrave;ng từ Italy gi&aacute; rất đắt, t&ocirc;i c&acirc;n nhắc thay thế bằng phụ kiện nhập từ Ấn Độ, Indonesia&hellip; Xe cổ l&agrave; th&uacute; chơi thật sự tốn k&eacute;m&quot;, anh n&oacute;i với&nbsp;<em>Zing</em>.</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/295048253_1028781894503847_7455024149699757062_n_2.jpg\" style=\"height:1200px; width:1600px\" /></p>\r\n\r\n<h3>Sẵn s&agrave;ng tốn k&eacute;m</h3>\r\n\r\n<p>Theo Tấn Sang, việc sở hữu c&ugrave;ng l&uacute;c 3 chiếc xe m&aacute;y khiến số tiền sửa chữa, bảo dưỡng bị độn l&ecirc;n nhiều. Linh kiện xe đắt đỏ, nhưng anh kh&ocirc;ng d&aacute;m thay thế bằng loại rẻ tiền bởi lo sợ hư hại phương tiện.</p>\r\n\r\n<p>&quot;Cứ khoảng 1-2 th&aacute;ng, t&ocirc;i lại mang xe đi bảo dưỡng một lần. Mỗi chiếc xe ti&ecirc;u tốn 500.000-1 triệu đồng cho một lần &#39;thăm kh&aacute;m&#39; như vậy. Chăm xe rất mệt, nếu kh&ocirc;ng kiểm tra thường xuy&ecirc;n xe sẽ dễ bị hỏng, khi đ&oacute; lại tốn k&eacute;m th&ecirc;m&quot;, anh t&acirc;m sự.</p>\r\n\r\n<p>Tương tự Tấn Sang, Ho&agrave;ng Duy (28 tuổi, B&igrave;nh Dương) cũng khẳng định th&uacute; chơi xe cổ ti&ecirc;u tốn cả thời gian, c&ocirc;ng sức v&agrave; tiền bạc.</p>\r\n\r\n<p>Anh hiện sở hữu Vespa mini 1963, Vespa Sprint v&agrave; Vespa Px đời cũ. Trong đ&oacute;, chiếc Vespa mini được anh mua v&agrave; t&acirc;n trang bộ m&aacute;y c&aacute;ch đ&acirc;y v&agrave;i năm với gi&aacute; hơn 100 triệu đồng.</p>\r\n\r\n<p>Theo Ho&agrave;ng Duy, chiếc Vespa mini khan hiếm tr&ecirc;n thị trường. Anh rất kh&oacute; khăn để t&igrave;m được một chiếc ưng &yacute;, đầy đủ giấy tờ với bộ m&aacute;y c&ograve;n tốt.</p>\r\n\r\n<p>&quot;T&iacute;nh đến hiện tại, xe cổ l&agrave; đam m&ecirc; tốn k&eacute;m nhất của t&ocirc;i&quot;, anh b&agrave;y tỏ.</p>\r\n\r\n<p>Thừa nhận tốn k&eacute;m, nhưng hầu hết người chơi xe cổ đều đ&aacute;nh gi&aacute; c&aacute;c d&ograve;ng xe cổ n&oacute;i chung v&agrave; Vespa cổ n&oacute;i ri&ecirc;ng hầu như kh&ocirc;ng mất gi&aacute;. Nhiều bạn trẻ c&ograve;n cho đ&acirc;y l&agrave; khoản đầu tư hợp l&yacute;, vừa phục vụ sở th&iacute;ch c&aacute; nh&acirc;n, vừa c&oacute; thể b&aacute;n lại m&agrave; vẫn đảm bảo gi&aacute; trị.</p>\r\n\r\n<p>Th&aacute;i Băng (24 tuổi, Đồng Nai) hiện sở hữu Vespa Sprint Veloce 1972 - một trong những chiếc xe cổ kh&aacute; hiếm tại Việt Nam. Anh cho rằng gi&aacute; xe cổ đang tăng rất nhanh ở thời điểm hiện tại.</p>\r\n\r\n<p>&quot;Những năm trước, gi&aacute; xe cổ rẻ v&igrave; kh&ocirc;ng c&oacute; phụ t&ugrave;ng để thay thế, sửa chữa. Nhưng v&agrave;i năm gần đ&acirc;y, lượng người quan t&acirc;m, t&igrave;m hiểu đến xe cổ tăng cao v&igrave; vấn đề phụ t&ugrave;ng đ&atilde; được giải quyết. Gi&aacute; trị xe cũng từ đ&oacute; m&agrave; được n&acirc;ng l&ecirc;n&quot;, anh l&yacute; giải.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, Th&aacute;i Băng cho rằng nếu xem xe cổ l&agrave; một h&igrave;nh thức đầu tư, người b&aacute;n cần phải c&oacute; kiến thức rất s&acirc;u rộng. C&ograve;n kh&ocirc;ng, họ chỉ n&ecirc;n xem đ&acirc;y l&agrave; một sở th&iacute;ch, th&uacute; chơi để kết nối bạn b&egrave;, cộng đồng y&ecirc;u xe.</p>\r\n\r\n<h3>K&eacute;n người chơi</h3>\r\n\r\n<p>Theo một số người trẻ sở hữu v&agrave; đam m&ecirc; xe cổ, họ kh&ocirc;ng chỉ phải d&agrave;nh tiền bạc v&agrave; thời gian để &quot;chăm s&oacute;c&quot; xe. B&ecirc;n cạnh đ&oacute;, mỗi người c&ograve;n cần trang bị nhiều kiến thức về m&aacute;y m&oacute;c, phụ t&ugrave;ng, đồng thời c&oacute; gu thẩm mỹ để mang lại cho chiếc xe vẻ ngo&agrave;i độc đ&aacute;o.</p>\r\n\r\n<p>Trước khi quyết định mua chiếc Vespa Sprint Veloce, Th&aacute;i Băng phải d&agrave;nh thời gian d&agrave;i nhằm t&igrave;m hiểu kỹ về đời xe v&agrave; c&aacute;ch thức bảo tr&igrave;, bảo dưỡng. Sau đ&oacute;, anh nhờ chuy&ecirc;n gia tư vấn xem m&igrave;nh n&ecirc;n giữ xe ở m&agrave;u sắc nguy&ecirc;n bản hay thay đổi để ph&ugrave; hợp c&aacute; t&iacute;nh.</p>\r\n\r\n<p>&quot;Chi ra số tiền kh&ocirc;ng nhỏ, t&ocirc;i nghi&ecirc;n cứu kỹ lưỡng về những phụ t&ugrave;ng thay thế cho xe, đặt mua hoặc săn l&ugrave;ng nếu xe y&ecirc;u cầu linh kiện hiếm. Đối với bất cứ tay chơi xe cổ n&agrave;o, việc t&igrave;m được một cửa h&agrave;ng sửa xe uy t&iacute;n với gi&aacute; cả phải chăng l&agrave; một th&aacute;ch thức lớn&quot;, anh n&oacute;i.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, anh cho rằng người chơi xe cần c&oacute; kiến thức r&otilde; r&agrave;ng về d&ograve;ng xe m&igrave;nh đang nhắm đến, nếu kh&ocirc;ng sẽ dẫn đến t&acirc;m l&yacute; dễ ch&aacute;n nản sau thời gian ngắn.</p>\r\n\r\n<p>&quot;Do xe l&acirc;u đời n&ecirc;n việc hư hỏng trong qu&aacute; tr&igrave;nh chơi l&agrave; điều kh&ocirc;ng thể tr&aacute;nh khỏi, phụ t&ugrave;ng nguy&ecirc;n bản cũng kh&oacute; kiếm v&agrave; rất đắt đỏ. Đ&acirc;y l&agrave; t&acirc;m l&yacute; chung m&agrave; người chơi xe cổ n&ecirc;n chuẩn bị trước&quot;, Th&aacute;i Băng chia sẻ.</p>\r\n\r\n<p>Tương tự như Th&aacute;i Băng, Ho&agrave;ng Duy cũng tốn nhiều thời gian để nghi&ecirc;n cứu thay đổi cho chiếc Vespa mini của m&igrave;nh. Anh đặt ra những y&ecirc;u cầu khắt khe cho người thợ sửa chữa nhằm đảm bảo chiếc xe c&oacute; ngoại h&igrave;nh đẹp v&agrave; chất lượng tốt nhất.</p>\r\n\r\n<p>&quot;T&ocirc;i t&igrave;m đến những thợ sơn chuy&ecirc;n nghiệp về Vespa. C&ograve;n phần m&aacute;y m&oacute;c, t&ocirc;i phải d&agrave;nh thời gian đồng h&agrave;nh c&ugrave;ng thợ m&aacute;y, quan s&aacute;t chi tiết c&aacute;c quy tr&igrave;nh sửa chữa. Khi chơi xe cổ, bạn kh&ocirc;ng cần qu&aacute; giỏi nhưng phải biết được thợ đang l&agrave;m g&igrave; với chiếc xe của m&igrave;nh&quot;, anh n&oacute;i.</p>\r\n\r\n<h3>Chỉ d&ugrave;ng xe cổ đi dạo phố</h3>\r\n\r\n<p>Một v&agrave;i d&ograve;ng xe cổ c&oacute; ph&acirc;n khối thấp, đ&acirc;y ch&iacute;nh l&agrave; yếu tố m&agrave; người mua xe cần c&acirc;n nhắc kỹ c&agrave;ng.</p>\r\n\r\n<p>Ho&agrave;ng Duy cho biết chiếc Vespa mini của m&igrave;nh chỉ c&oacute; ph&acirc;n khối 50cc. Anh chi th&ecirc;m 40 triệu đồng để độ lại bộ m&aacute;y cho phương tiện n&agrave;y.</p>\r\n\r\n<p>Tấn Sang cũng l&agrave; trường hợp tương tự. Chiếc Honda 1986 của anh chỉ c&oacute; thể d&ugrave;ng để đi c&agrave; ph&ecirc;, dạo phố m&agrave; kh&ocirc;ng thể đi xa. Anh đ&agrave;nh bỏ chiếc xe n&agrave;y v&agrave;o garage thỏa đam m&ecirc; sưu tầm.</p>\r\n\r\n<p>&quot;Chơi xe cổ, nhưng ch&uacute;ng t&ocirc;i đều c&oacute; th&ecirc;m một chiếc xe m&aacute;y th&ocirc;ng dụng kh&aacute;c để đi. Xe cổ kh&ocirc;ng th&iacute;ch hợp để đi dưới mưa, đường đất hoặc đi v&agrave;o trời tối khuya bởi kh&aacute; nguy hiểm&quot;, anh cho hay.</p>\r\n\r\n<p>Theo Tấn Sang, để c&oacute; thể gắn b&oacute; l&acirc;u với th&uacute; chơi xe, người trẻ n&ecirc;n tham gia những hội nh&oacute;m xe cổ để được hỗ trợ v&agrave; chia sẻ kinh nghiệm. Đ&acirc;y cũng l&agrave; nơi mọi người mở rộng mối quan hệ, c&oacute; th&ecirc;m những người bạn v&agrave; duy tr&igrave; niềm đam m&ecirc;.</p>\r\n\r\n<p>&quot;Gi&aacute; trị của chiếc xe cổ nằm ở t&iacute;nh nguy&ecirc;n bản, gắn liền với những cột mốc thời gian. C&ograve;n nếu y&ecirc;u cầu một phương tiện đi lại thuận tiện, kh&ocirc;ng ngại mưa, gi&oacute;, t&ocirc;i nghĩ bạn kh&ocirc;ng n&ecirc;n theo đuổi loại xe n&agrave;y&quot;, Tấn Sang bộc bạch.</p>', 'Nguyễn Văn A', 0, 1, '22080391755pexels_pixabay_221299.jpg', 'publish', '2022-08-03 02:17:55', NULL),
(9, 'Trào lưu nuôi cua kiểng đắt tiền thay thú cưng', 'trao-luu-nuoi-cua-kieng-dat-tien-thay-thu-cung', '<p>Tham gia hội chơi cua kiểng được v&agrave;i th&aacute;ng, Trung Ki&ecirc;n (22 tuổi, quận Đống Đa, H&agrave; Nội) h&agrave;o hứng khi sở hữu 5 th&uacute; cưng c&oacute; v&oacute;c d&aacute;ng v&agrave; m&agrave;u sắc đẹp mắt.</p>\r\n\r\n<p>L&agrave; người chơi c&aacute; cảnh l&acirc;u năm, Ki&ecirc;n cho biết từ ng&agrave;y nu&ocirc;i những loại cua độc đ&aacute;o n&agrave;y, anh đ&atilde; tạm dừng mua th&ecirc;m c&aacute; v&agrave; đầu tư thời gian, tiền bạc cho th&uacute; chơi mới.</p>\r\n\r\n<p>&ldquo;C&aacute; th&igrave; t&ocirc;i chỉ nu&ocirc;i một bể nhưng với mấy &#39;b&eacute; cưng&#39; n&agrave;y, t&ocirc;i đầu tư cho mỗi con một kh&ocirc;ng gian sống ri&ecirc;ng, mỗi ng&agrave;y d&agrave;nh v&agrave;i tiếng chỉ để ngắm ngh&iacute;a ch&uacute;ng&rdquo;, Ki&ecirc;n cho biết.</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/cua_zing_113.jpg\" style=\"height:936px; width:936px\" /></p>\r\n\r\n<h3>Th&uacute; chơi lạ mắt</h3>\r\n\r\n<p>Vừa lấy t&eacute;p cho cua ăn, Ki&ecirc;n vừa n&oacute;i, cua kiểng anh nu&ocirc;i c&oacute; 4 con thuộc giống Cua đ&aacute; c&agrave;ng đỏ (Perisesarma Bidens) v&agrave; 1 con cua thiết gi&aacute;p (Indochinamon).</p>\r\n\r\n<p>&ldquo;Gi&aacute; cua kiểng được t&iacute;nh theo k&iacute;ch thước đo từ chiều ngang của mai, từ 3 cm (cỡ b&eacute; nhất) đến 7 cm (cỡ đại), cua c&agrave;ng lớn c&agrave;ng đắt. Ban đầu, t&ocirc;i nghĩ nu&ocirc;i cua kh&oacute; v&igrave; kh&ocirc;ng c&oacute; kinh nghiệm, tuy nhi&ecirc;n khi chăm s&oacute;c th&igrave; thấy ch&uacute;ng dễ nu&ocirc;i. T&ocirc;i nu&ocirc;i cua bể cạn, đến bữa cho ăn t&eacute;p, giun hoặc c&aacute; con l&agrave; ch&uacute;ng đ&atilde; sống được khỏe mạnh, kh&ocirc;ng tốn thời gian&rdquo;, anh Ki&ecirc;n chia sẻ.</p>\r\n\r\n<p>C&ograve;n với Ho&agrave;ng H&agrave; (23 tuổi, quận Cầu Giấy, H&agrave; Nội), sau v&agrave;i tuần t&igrave;m hiểu, anh chọn ngay 2 con cua kiểng cỡ đại c&oacute; t&ecirc;n Hainan potamon rubrum (loại cua nước ngọt trong họ cua n&uacute;i) với gi&aacute; hơn 400.000 đồng.</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/cua_zing_14_1.jpg\" style=\"height:422px; width:670px\" /></p>\r\n\r\n<p>&ldquo;Ngay từ khi thấy h&igrave;nh ảnh cua n&agrave;y tr&ecirc;n mạng, t&ocirc;i bị thu h&uacute;t bởi m&agrave;u sắc v&agrave; k&iacute;ch thước của ch&uacute;ng, hiếm khi được nh&igrave;n loại cua c&agrave;ng cong đẹp đến thế. Hiện tại t&ocirc;i c&oacute; nu&ocirc;i t&ocirc;m, c&aacute;, r&ugrave;a, ốc&hellip; nay lại c&oacute; th&ecirc;m cua cảnh v&agrave;o &#39;bộ sưu tập kiểng&#39; của m&igrave;nh&rdquo;, H&agrave; n&oacute;i.</p>\r\n\r\n<p>Cua Hainan potamon rubrum c&oacute; thể t&igrave;m thấy trong tự nhi&ecirc;n ở c&aacute;c nước Đ&ocirc;ng Dương, nhưng rất hiếm người bắt được v&igrave; ch&uacute;ng sống s&acirc;u trong hốc đ&aacute; s&ocirc;ng, suối.</p>\r\n\r\n<p>&quot;Loại cua n&agrave;y v&ocirc; hại, t&ocirc;i mua cua đ&atilde; được thuần h&oacute;a n&ecirc;n nu&ocirc;i b&aacute;n cạn v&agrave; thả trong bể c&aacute; cảnh&rdquo;, H&agrave; cho biết.</p>\r\n\r\n<p>Th&uacute; chơi cua kiểng xuất hiện tr&ecirc;n thế giới từ những năm 2010, phổ biến nhất ở Th&aacute;i Lan, Trung Quốc.</p>\r\n\r\n<p>Với sự ph&aacute;t triển của mạng x&atilde; hội, người d&ugrave;ng dễ d&agrave;ng t&igrave;m địa chỉ b&aacute;n cua bằng c&aacute;c từ kh&oacute;a &quot;cua kiểng&quot;, &quot;cua cảnh&quot;. Gần đ&acirc;y, c&aacute;c hội nh&oacute;m về cua kiểng xuất hiện h&agrave;ng loạt, với v&agrave;i ngh&igrave;n người tham gia.</p>\r\n\r\n<p>Chị Anh Thơ (30 tuổi, quận 1, TP.HCM) chi gần 1 triệu đồng đặt mua 4 con cua đủ m&agrave;u sắc từ H&agrave; Nội.</p>\r\n\r\n<p>&ldquo;Chơi chưa đầy một th&aacute;ng nhưng ng&agrave;y n&agrave;o t&ocirc;i cũng nghi&ecirc;n cứu để t&igrave;m mua những giống cua mới. Chiếc bể b&agrave;y giữa nh&agrave; th&agrave;nh nơi y&ecirc;u th&iacute;ch mới của t&ocirc;i v&agrave; bạn b&egrave; tới chơi. V&agrave;i người bạn của t&ocirc;i cũng m&ecirc; v&agrave; bắt tay v&agrave;o nu&ocirc;i. Tr&ecirc;n mạng, nhiều hội nh&oacute;m được lập ra để chia sẻ giống cua, c&aacute;c c&aacute;ch chăm s&oacute;c&quot;, chị Thơ kể.</p>\r\n\r\n<p>Tương tự chị Thơ, anh Anh Khoa (quận 10, TP.HCM) c&ograve;n đầu tư x&acirc;y nhiều bể b&aacute;n cạn nu&ocirc;i h&agrave;ng trăm con cua.</p>\r\n\r\n<p>&quot;Một chiếc bể đẹp với h&ograve;n non bộ, rễ c&acirc;y, tiểu cảnh vừa đẹp, vừa l&agrave; m&ocirc;i trường sống tốt cho cua. Kh&aacute;c với c&aacute;, những loại bể n&agrave;y phải c&oacute; cả bờ để cua tr&egrave;o l&ecirc;n. Muốn cua sống khỏe phải đầu tư chăm s&oacute;c, coi ch&uacute;ng như th&uacute; cưng th&igrave; mới gắn b&oacute; được&quot;, anh Khoa giải th&iacute;ch.</p>\r\n\r\n<h3>Kh&ocirc;ng dễ chăm s&oacute;c</h3>\r\n\r\n<p>Anh H&agrave; Xu&acirc;n Lộc (34 tuổi, chủ chuỗi cửa h&agrave;ng cung cấp cua cảnh tại H&agrave; Nội, Đ&agrave; Lạt v&agrave; TP.HCM), người hơn 10 năm chơi cua kiểng, cho biết cua nhiều m&agrave;u c&oacute; thể do nguồn nước, chất đất, một số loại cua sống ở chất đất m&agrave;u đen sẽ c&oacute; m&agrave;u xanh biển hoặc xanh l&aacute;. Cua sống đất đồi, đất đỏ th&igrave; c&oacute; m&agrave;u đỏ, v&agrave;ng chưa kể bị đột biến th&igrave; sẽ tạo ra nhiều m&agrave;u kh&aacute;c.</p>\r\n\r\n<p>&quot;M&agrave;u sắc c&ograve;n phụ thuộc v&agrave;o dinh dưỡng, chất lượng thức ăn k&eacute;m khiến cua c&oacute; m&agrave;u nhạt đi, thậm ch&iacute; chết trước khi sinh sản. Một con cua đạt chuẩn phải c&oacute; m&agrave;u sắc r&otilde; n&eacute;t, th&acirc;n kh&ocirc;ng vết xước, đủ ch&acirc;n, c&agrave;ng&rdquo;, anh Lộc chia sẻ.</p>\r\n\r\n<p>Năm 2016, anh Lộc bắt đầu t&igrave;m hiểu v&agrave; thử nh&acirc;n giống nh&aacute;nh cua Hainan potamon. Thời gian đầu chưa c&oacute; kỹ thuật, phương ph&aacute;p, to&agrave;n bộ cua giống đều chết.</p>\r\n\r\n<p>Tham gia hội chơi cua kiểng được v&agrave;i th&aacute;ng, Trung Ki&ecirc;n (22 tuổi, quận Đống Đa, H&agrave; Nội) h&agrave;o hứng khi sở hữu 5 th&uacute; cưng c&oacute; v&oacute;c d&aacute;ng v&agrave; m&agrave;u sắc đẹp mắt.</p>\r\n\r\n<p>L&agrave; người chơi c&aacute; cảnh l&acirc;u năm, Ki&ecirc;n cho biết từ ng&agrave;y nu&ocirc;i những loại cua độc đ&aacute;o n&agrave;y, anh đ&atilde; tạm dừng mua th&ecirc;m c&aacute; v&agrave; đầu tư thời gian, tiền bạc cho th&uacute; chơi mới.</p>\r\n\r\n<p>&ldquo;C&aacute; th&igrave; t&ocirc;i chỉ nu&ocirc;i một bể nhưng với mấy &#39;b&eacute; cưng&#39; n&agrave;y, t&ocirc;i đầu tư cho mỗi con một kh&ocirc;ng gian sống ri&ecirc;ng, mỗi ng&agrave;y d&agrave;nh v&agrave;i tiếng chỉ để ngắm ngh&iacute;a ch&uacute;ng&rdquo;, Ki&ecirc;n cho biết.</p>\r\n\r\n<p>Cua thiết gi&aacute;p (Indochinamon) với vẻ ngo&agrave;i hầm hố, bộ vỏ to v&agrave; sần s&ugrave;i đang được nhiều người chơi ưa th&iacute;ch.</p>\r\n\r\n<ul>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n</ul>\r\n\r\n<p><img alt=\"Thu choi cua kieng anh 2\" src=\"https://znews-photo.zingcdn.me/w1920/Uploaded/aohvtsw/2022_08_01/cua_zing_112.jpg\" style=\"height:936px; width:936px\" /></p>\r\n\r\n<p>Cua thiết gi&aacute;p (Indochinamon) với vẻ ngo&agrave;i hầm hố, bộ vỏ to v&agrave; sần s&ugrave;i đang được nhiều người chơi ưa th&iacute;ch.</p>\r\n\r\n<ul>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n</ul>\r\n\r\n<p><img alt=\"Thu choi cua kieng anh 1\" src=\"https://znews-photo.zingcdn.me/w1920/Uploaded/aohvtsw/2022_08_01/cua_zing_113.jpg\" style=\"height:936px; width:936px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"Thu choi cua kieng anh 1\" src=\"https://znews-photo.zingcdn.me/w1200/Uploaded/aohvtsw/2022_08_01/cua_zing_113.jpg\" style=\"height:936px; width:936px\" title=\"Thú chơi cua kiểng  ảnh 1\" /></p>\r\n\r\n			<p><img alt=\"Thu choi cua kieng anh 2\" src=\"https://znews-photo.zingcdn.me/w1200/Uploaded/aohvtsw/2022_08_01/cua_zing_112.jpg\" style=\"height:936px; width:936px\" title=\"Thú chơi cua kiểng  ảnh 2\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Cua thiết gi&aacute;p (Indochinamon) với vẻ ngo&agrave;i hầm hố, bộ vỏ to v&agrave; sần s&ugrave;i đang được nhiều người chơi ưa th&iacute;ch.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h3>Th&uacute; chơi lạ mắt</h3>\r\n\r\n<p>Vừa lấy t&eacute;p cho cua ăn, Ki&ecirc;n vừa n&oacute;i, cua kiểng anh nu&ocirc;i c&oacute; 4 con thuộc giống Cua đ&aacute; c&agrave;ng đỏ (Perisesarma Bidens) v&agrave; 1 con cua thiết gi&aacute;p (Indochinamon).</p>\r\n\r\n<p>&ldquo;Gi&aacute; cua kiểng được t&iacute;nh theo k&iacute;ch thước đo từ chiều ngang của mai, từ 3 cm (cỡ b&eacute; nhất) đến 7 cm (cỡ đại), cua c&agrave;ng lớn c&agrave;ng đắt. Ban đầu, t&ocirc;i nghĩ nu&ocirc;i cua kh&oacute; v&igrave; kh&ocirc;ng c&oacute; kinh nghiệm, tuy nhi&ecirc;n khi chăm s&oacute;c th&igrave; thấy ch&uacute;ng dễ nu&ocirc;i. T&ocirc;i nu&ocirc;i cua bể cạn, đến bữa cho ăn t&eacute;p, giun hoặc c&aacute; con l&agrave; ch&uacute;ng đ&atilde; sống được khỏe mạnh, kh&ocirc;ng tốn thời gian&rdquo;, anh Ki&ecirc;n chia sẻ.</p>\r\n\r\n<p>C&ograve;n với Ho&agrave;ng H&agrave; (23 tuổi, quận Cầu Giấy, H&agrave; Nội), sau v&agrave;i tuần t&igrave;m hiểu, anh chọn ngay 2 con cua kiểng cỡ đại c&oacute; t&ecirc;n Hainan potamon rubrum (loại cua nước ngọt trong họ cua n&uacute;i) với gi&aacute; hơn 400.000 đồng.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Thu choi cua kieng anh 3\" src=\"https://znews-photo.zingcdn.me/w860/Uploaded/aohvtsw/2022_08_01/cua_zing_14_1.jpg\" style=\"height:422px; width:670px\" title=\"Thú chơi cua kiểng  ảnh 3\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>2 con cua kiểng cỡ đại c&oacute; t&ecirc;n Hainan potamon rubrum của anh H&agrave;.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&ldquo;Ngay từ khi thấy h&igrave;nh ảnh cua n&agrave;y tr&ecirc;n mạng, t&ocirc;i bị thu h&uacute;t bởi m&agrave;u sắc v&agrave; k&iacute;ch thước của ch&uacute;ng, hiếm khi được nh&igrave;n loại cua c&agrave;ng cong đẹp đến thế. Hiện tại t&ocirc;i c&oacute; nu&ocirc;i t&ocirc;m, c&aacute;, r&ugrave;a, ốc&hellip; nay lại c&oacute; th&ecirc;m cua cảnh v&agrave;o &#39;bộ sưu tập kiểng&#39; của m&igrave;nh&rdquo;, H&agrave; n&oacute;i.</p>\r\n\r\n<p>Cua Hainan potamon rubrum c&oacute; thể t&igrave;m thấy trong tự nhi&ecirc;n ở c&aacute;c nước Đ&ocirc;ng Dương, nhưng rất hiếm người bắt được v&igrave; ch&uacute;ng sống s&acirc;u trong hốc đ&aacute; s&ocirc;ng, suối.</p>\r\n\r\n<p>&quot;Loại cua n&agrave;y v&ocirc; hại, t&ocirc;i mua cua đ&atilde; được thuần h&oacute;a n&ecirc;n nu&ocirc;i b&aacute;n cạn v&agrave; thả trong bể c&aacute; cảnh&rdquo;, H&agrave; cho biết.</p>\r\n\r\n<p>Th&uacute; chơi cua kiểng xuất hiện tr&ecirc;n thế giới từ những năm 2010, phổ biến nhất ở Th&aacute;i Lan, Trung Quốc.</p>\r\n\r\n<p>Với sự ph&aacute;t triển của mạng x&atilde; hội, người d&ugrave;ng dễ d&agrave;ng t&igrave;m địa chỉ b&aacute;n cua bằng c&aacute;c từ kh&oacute;a &quot;cua kiểng&quot;, &quot;cua cảnh&quot;. Gần đ&acirc;y, c&aacute;c hội nh&oacute;m về cua kiểng xuất hiện h&agrave;ng loạt, với v&agrave;i ngh&igrave;n người tham gia.</p>\r\n\r\n<p>Chị Anh Thơ (30 tuổi, quận 1, TP.HCM) chi gần 1 triệu đồng đặt mua 4 con cua đủ m&agrave;u sắc từ H&agrave; Nội.</p>\r\n\r\n<p>&ldquo;Chơi chưa đầy một th&aacute;ng nhưng ng&agrave;y n&agrave;o t&ocirc;i cũng nghi&ecirc;n cứu để t&igrave;m mua những giống cua mới. Chiếc bể b&agrave;y giữa nh&agrave; th&agrave;nh nơi y&ecirc;u th&iacute;ch mới của t&ocirc;i v&agrave; bạn b&egrave; tới chơi. V&agrave;i người bạn của t&ocirc;i cũng m&ecirc; v&agrave; bắt tay v&agrave;o nu&ocirc;i. Tr&ecirc;n mạng, nhiều hội nh&oacute;m được lập ra để chia sẻ giống cua, c&aacute;c c&aacute;ch chăm s&oacute;c&quot;, chị Thơ kể.</p>\r\n\r\n<p>Những con cua Hainan potamon rubrum do anh Lộc tự tay chăm s&oacute;c.</p>\r\n\r\n<ul>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n</ul>\r\n\r\n<p><img src=\"https://znews-photo.zingcdn.me/w1920/Uploaded/aohvtsw/2022_08_01/cua_zing_17_2.jpg\" style=\"height:800px; width:1203px\" /></p>\r\n\r\n<p>Những con cua Hainan potamon rubrum do anh Lộc tự tay chăm s&oacute;c.</p>\r\n\r\n<ul>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n</ul>\r\n\r\n<p><img alt=\"Thu choi cua kieng anh 4\" src=\"https://znews-photo.zingcdn.me/w1920/Uploaded/aohvtsw/2022_08_01/cua_zing_15.jpg\" style=\"height:1078px; width:1430px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"Thu choi cua kieng anh 4\" src=\"https://znews-photo.zingcdn.me/w1200/Uploaded/aohvtsw/2022_08_01/cua_zing_15.jpg\" style=\"height:1078px; width:1430px\" title=\"Thú chơi cua kiểng  ảnh 4\" /></p>\r\n\r\n			<p><img src=\"https://znews-photo.zingcdn.me/w1200/Uploaded/aohvtsw/2022_08_01/cua_zing_17_2.jpg\" style=\"height:800px; width:1203px\" title=\"null\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Những con cua Hainan potamon rubrum do anh Lộc tự tay chăm s&oacute;c.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Tương tự chị Thơ, anh Anh Khoa (quận 10, TP.HCM) c&ograve;n đầu tư x&acirc;y nhiều bể b&aacute;n cạn nu&ocirc;i h&agrave;ng trăm con cua.</p>\r\n\r\n<p>&quot;Một chiếc bể đẹp với h&ograve;n non bộ, rễ c&acirc;y, tiểu cảnh vừa đẹp, vừa l&agrave; m&ocirc;i trường sống tốt cho cua. Kh&aacute;c với c&aacute;, những loại bể n&agrave;y phải c&oacute; cả bờ để cua tr&egrave;o l&ecirc;n. Muốn cua sống khỏe phải đầu tư chăm s&oacute;c, coi ch&uacute;ng như th&uacute; cưng th&igrave; mới gắn b&oacute; được&quot;, anh Khoa giải th&iacute;ch.</p>\r\n\r\n<h3>Kh&ocirc;ng dễ chăm s&oacute;c</h3>\r\n\r\n<p>Anh H&agrave; Xu&acirc;n Lộc (34 tuổi, chủ chuỗi cửa h&agrave;ng cung cấp cua cảnh tại H&agrave; Nội, Đ&agrave; Lạt v&agrave; TP.HCM), người hơn 10 năm chơi cua kiểng, cho biết cua nhiều m&agrave;u c&oacute; thể do nguồn nước, chất đất, một số loại cua sống ở chất đất m&agrave;u đen sẽ c&oacute; m&agrave;u xanh biển hoặc xanh l&aacute;. Cua sống đất đồi, đất đỏ th&igrave; c&oacute; m&agrave;u đỏ, v&agrave;ng chưa kể bị đột biến th&igrave; sẽ tạo ra nhiều m&agrave;u kh&aacute;c.</p>\r\n\r\n<p>&quot;M&agrave;u sắc c&ograve;n phụ thuộc v&agrave;o dinh dưỡng, chất lượng thức ăn k&eacute;m khiến cua c&oacute; m&agrave;u nhạt đi, thậm ch&iacute; chết trước khi sinh sản. Một con cua đạt chuẩn phải c&oacute; m&agrave;u sắc r&otilde; n&eacute;t, th&acirc;n kh&ocirc;ng vết xước, đủ ch&acirc;n, c&agrave;ng&rdquo;, anh Lộc chia sẻ.</p>\r\n\r\n<p>Năm 2016, anh Lộc bắt đầu t&igrave;m hiểu v&agrave; thử nh&acirc;n giống nh&aacute;nh cua Hainan potamon. Thời gian đầu chưa c&oacute; kỹ thuật, phương ph&aacute;p, to&agrave;n bộ cua giống đều chết.</p>\r\n\r\n<p>Những chiếc hang do anh Khoa s&aacute;ng tạo để cua l&ecirc;n bờ.</p>\r\n\r\n<ul>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n</ul>\r\n\r\n<p><img alt=\"Thu choi cua kieng anh 6\" src=\"https://znews-photo.zingcdn.me/w1920/Uploaded/aohvtsw/2022_08_01/cua_zing_18.jpg\" style=\"height:806px; width:1430px\" /></p>\r\n\r\n<p>Những chiếc hang do anh Khoa s&aacute;ng tạo để cua l&ecirc;n bờ.</p>\r\n\r\n<ul>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n</ul>\r\n\r\n<p><img alt=\"Thu choi cua kieng anh 5\" src=\"https://znews-photo.zingcdn.me/w1920/Uploaded/aohvtsw/2022_08_01/cua_zing_19.jpg\" style=\"height:528px; width:936px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"Thu choi cua kieng anh 5\" src=\"https://znews-photo.zingcdn.me/w1200/Uploaded/aohvtsw/2022_08_01/cua_zing_19.jpg\" style=\"height:528px; width:936px\" title=\"Thú chơi cua kiểng  ảnh 5\" /></p>\r\n\r\n			<p><img alt=\"Thu choi cua kieng anh 6\" src=\"https://znews-photo.zingcdn.me/w1200/Uploaded/aohvtsw/2022_08_01/cua_zing_18.jpg\" style=\"height:806px; width:1430px\" title=\"Thú chơi cua kiểng  ảnh 6\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Những chiếc hang do anh Khoa s&aacute;ng tạo để cua l&ecirc;n bờ.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Sau 2 năm nghi&ecirc;n cứu, dưới sự hướng dẫn của c&aacute;c chuy&ecirc;n gia về cua nước ngọt, anh đ&atilde; th&agrave;nh c&ocirc;ng v&agrave; mở cửa h&agrave;ng, gi&uacute;p người chơi dễ d&agrave;ng sở hữu những th&uacute; cưng độc lạ.</p>\r\n\r\n<p>Từ đầu năm đến nay, trung b&igrave;nh mỗi th&aacute;ng anh H&agrave; Xu&acirc;n Lộc b&aacute;n hơn 1.000 con cho kh&aacute;ch h&agrave;ng tr&ecirc;n khắp cả nước.</p>\r\n\r\n<p>&ldquo;Đối tượng kh&aacute;ch h&agrave;ng đa phần l&agrave; người trẻ, thời gian gần đ&acirc;y cửa h&agrave;ng c&oacute; kh&aacute; nhiều kh&aacute;ch h&agrave;ng quay trở lại v&igrave; muốn mua th&ecirc;m. Nhiều kh&aacute;ch mua một lần cả chục con đủ m&agrave;u sắc. Th&uacute; chơi n&agrave;y kh&oacute; bỏ v&igrave; ch&iacute;nh t&ocirc;i đ&atilde; theo đuổi từ 2012 đến nay. Nhiều khi t&ocirc;i kh&ocirc;ng đủ h&agrave;ng để b&aacute;n v&igrave; chưa đến đợt cua sinh sản&rdquo;, anh Lộc n&oacute;i.</p>\r\n\r\n<p>Theo anh, cua cảnh cung cấp ra thị trường thường c&oacute; nguồn gốc từ Đ&ocirc;ng Dương, được thuần h&oacute;a, th&iacute;ch nghi với thời tiết, độ ẩm ở Việt Nam, &iacute;t bị bệnh. Tuổi thọ của ch&uacute;ng c&oacute; thể l&ecirc;n tới hơn chục năm nếu biết c&aacute;ch chăm s&oacute;c.</p>\r\n\r\n<p>&quot;V&igrave; nhu cầu ng&agrave;y c&agrave;ng cao, người chơi cần thận trọng với cua cảnh gi&aacute; rẻ bởi c&oacute; thể ch&uacute;ng mới được bắt từ tự nhi&ecirc;n, chưa thuần h&oacute;a, mang nhiều mầm bệnh (đa số l&agrave; c&aacute;c loại k&iacute; sinh tr&ugrave;ng như giun, s&aacute;n, ấu tr&ugrave;ng đỉa, rất dễ l&acirc;y nhiễm sang cho người), kh&ocirc;ng quen m&ocirc;i trường sống trong bể, rất dễ chết. Người mới nếu c&oacute; nhu cầu nu&ocirc;i cua phải t&igrave;m nguồn cẩn trọng để được tư vấn thức ăn, loại bể v&agrave; c&aacute;ch chăm s&oacute;c&quot;, anh th&ocirc;ng tin th&ecirc;m.</p>', 'Nguyễn Văn A', 0, 1, '22080392044cua_zing_14_1.jpg', 'publish', '2022-08-03 02:20:44', NULL),
(10, 'Xác cá mập thành nơi khách chụp ảnh', 'xac-ca-map-thanh-noi-khach-chup-anh', '<p>Sau khi vấp phải phản ứng ti&ecirc;u cực từ kh&aacute;ch h&agrave;ng, si&ecirc;u thị ở Seoul (H&agrave;n Quốc) đ&atilde; nhanh ch&oacute;ng ngừng hoạt động trưng b&agrave;y x&aacute;c c&aacute; mập.</p>\r\n\r\n<p>Ng&agrave;y 31/7, si&ecirc;u thị lớn ở ga Yongsan, Seoul hứng nhiều chỉ tr&iacute;ch khi trưng b&agrave;y x&aacute;c một con c&aacute; mập ở khu chụp ảnh quầy hải sản. H&agrave;nh động n&agrave;y được giới thiệu l&agrave; nhằm cung cấp &quot;trải nghiệm độc đ&aacute;o&quot; cho kh&aacute;ch h&agrave;ng, theo&nbsp;<em>Korea Times.</em></p>\r\n\r\n<p>Trong những bức ảnh được kh&aacute;ch h&agrave;ng đi ngang v&agrave; chụp lại, đăng tr&ecirc;n mạng x&atilde; hội, con c&aacute; mập c&ograve;n chưa được l&agrave;m sạch m&aacute;u được đặt tr&ecirc;n lớp đ&aacute;, tr&ecirc;n đầu l&agrave; tấm biển ghi: &quot;Cẩn thận, n&oacute; sẽ cắn nếu bạn chạm v&agrave;o đấy&quot;.</p>\r\n\r\n<p>Một tấm biển kh&aacute;c cho biết đ&acirc;y l&agrave; &quot;sự kiện độc quyền tại Yongsan E-mart&quot; cũng như ghi c&aacute;c đặc trưng của con vật, c&aacute;c m&oacute;n ăn sử dụng thịt c&aacute; mập, ảnh nh&acirc;n vật c&aacute; mập hoạt h&igrave;nh nổi tiếng. Si&ecirc;u thị c&ograve;n chuẩn bị một chiếc mũ h&igrave;nh c&aacute; mập để kh&aacute;ch chụp ảnh check-in v&agrave; đăng l&ecirc;n mạng.</p>\r\n\r\n<p>&quot;T&ocirc;i thấy những con c&aacute; mập sống trong thủy cung đ&atilde; tội lắm rồi, vậy m&agrave; t&ocirc;i c&ograve;n được mời chụp ảnh với một con c&aacute; mập chết, đang rỉ m&aacute;u&quot;, một người cảm th&aacute;n. Nhiều người kh&aacute;c cũng cho biết đ&atilde; bị sốc khi thấy x&aacute;c con c&aacute; thật được trưng b&agrave;y thay v&igrave; m&ocirc; h&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/cua_zing_14_1.jpg\" style=\"height:422px; width:670px\" /></p>\r\n\r\n<p>Từ th&aacute;ng 5, si&ecirc;u thị chi nh&aacute;nh Yongsan n&agrave;y đ&atilde; thi thoảng trưng b&agrave;y những lo&agrave;i c&aacute; lạ m&agrave; kh&aacute;ch hiếm khi thấy, v&iacute; dụ như c&aacute; đuối khổng lồ, c&aacute; mặt trăng, c&aacute; cam sọc để mang lại &quot;trải nghiệm kh&aacute;c thường&quot;.</p>\r\n\r\n<p>Ng&agrave;y 1/8, một l&atilde;nh đạo si&ecirc;u thị cho biết con c&aacute; mập n&agrave;y kh&ocirc;ng được b&aacute;n cho kh&aacute;ch h&agrave;ng. Những con vật từng trưng b&agrave;y &quot;được đ&aacute;nh bắt tự nhi&ecirc;n v&agrave; thải loại từ t&agrave;u đ&aacute;nh c&aacute;&quot;. Si&ecirc;u thị kh&ocirc;ng chủ đ&iacute;ch đ&aacute;nh bắt c&aacute; mập để trưng b&agrave;y.</p>\r\n\r\n<p>Trước những phản hồi ti&ecirc;u cực của kh&aacute;ch h&agrave;ng, si&ecirc;u thị đ&atilde; dừng hoạt động trưng b&agrave;y v&agrave; gửi lời xin lỗi v&igrave; đ&atilde; chuẩn bị sự kiện kh&ocirc;ng ph&ugrave; hợp với t&acirc;m l&yacute; kh&aacute;ch h&agrave;ng. Nơi n&agrave;y cũng c&oacute; kế hoạch thu thập v&agrave; lưu &yacute; những phản hồi của kh&aacute;ch để xem x&eacute;t chuẩn bị cho bất kỳ sự kiện trưng b&agrave;y n&agrave;o sắp tới.</p>', 'Nguyễn Văn A', 2, 1, '22080392316optimize_1_.jpg', 'publish', '2022-08-03 02:23:16', NULL);
INSERT INTO `posts` (`pid`, `title`, `slug`, `description`, `author`, `view`, `category_id`, `image`, `status`, `created_at`, `updated_at`) VALUES
(11, 'Cây cao nhất thế giới chết dần vì khách du lịch', 'cay-cao-nhat-the-gioi-chet-dan-vi-khach-du-lich', '<p>Hyperion, được Kỷ lục Guinness chứng nhận l&agrave; c&aacute;i c&acirc;y cao nhất thế giới, ch&iacute;nh thức kh&ocirc;ng c&ograve;n đ&oacute;n tiếp du kh&aacute;ch gh&eacute; thăm.</p>\r\n\r\n<p>C&ocirc;ng vi&ecirc;n Quốc gia Redwood (bang California, Mỹ) đưa ra tuy&ecirc;n bố v&agrave;o tuần trước rằng bất kỳ ai bị ph&aacute;t hiện đứng gần c&aacute;i c&acirc;y c&oacute; thể đối mặt với &aacute;n t&ugrave; 6 th&aacute;ng v&agrave; khoản phạt&nbsp;5.000 USD, theo&nbsp;<em>CNN</em>.</p>\r\n\r\n<p>C&acirc;y Hyperion l&agrave; một c&acirc;y gỗ đỏ cao hơn 115 m, được đặt t&ecirc;n theo một vị thần trong thần thoại Hy Lạp.</p>\r\n\r\n<p>N&oacute; nằm s&acirc;u trong c&ocirc;ng vi&ecirc;n v&agrave; kh&ocirc;ng c&oacute; đường m&ograve;n n&agrave;o dẫn đến vị tr&iacute; của c&aacute;i c&acirc;y. Năm 2006, n&oacute; được t&igrave;m thấy bởi hai nh&agrave; tự nhi&ecirc;n học Chris Atkins v&agrave; Michael Taylor.</p>\r\n\r\n<p>Vị tr&iacute; cụ thể của c&acirc;y kh&ocirc;ng được c&aacute;c nh&agrave; khoa học tiết lộ v&igrave; lo ngại du kh&aacute;ch c&oacute; thể l&agrave;m hại n&oacute;.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, những người đam m&ecirc; phi&ecirc;u lưu mạo hiểm vẫn t&igrave;m c&aacute;ch tiếp cận Hyperion suốt nhiều năm qua, khiến c&aacute;i c&acirc;y cao nhất thế giới đang phải đối mặt với sự suy tho&aacute;i m&ocirc;i trường nghi&ecirc;m trọng.</p>\r\n\r\n<p>&ldquo;Hyperion nằm ngo&agrave;i con đường m&ograve;n xuy&ecirc;n qua thảm thực vật d&agrave;y đặc, v&agrave; y&ecirc;u cầu du kh&aacute;ch phải trải qua h&agrave;nh tr&igrave;nh sinh tồn trong hoang d&atilde; để tiếp cận được n&oacute;&rdquo;, tr&iacute;ch tuy&ecirc;n bố tr&ecirc;n website của c&ocirc;ng vi&ecirc;n.</p>\r\n\r\n<p>Thế nhưng, bất chấp h&agrave;nh tr&igrave;nh di chuyển kh&oacute; khăn, c&aacute;i c&acirc;y cao nhất thế giới vẫn tiếp tục thu h&uacute;t người t&igrave;m đến do b&agrave;i đăng từ c&aacute;c blogger v&agrave; trang web du lịch. Điều n&agrave;y khiến cho m&ocirc;i trường sống xung quanh c&aacute;i c&acirc;y bị t&agrave;n ph&aacute; nghi&ecirc;m trọng.</p>\r\n\r\n<p>&ldquo;Với tư c&aacute;ch l&agrave; một du kh&aacute;ch, bạn phải quyết định xem liệu bạn sẽ g&oacute;p phần bảo tồn cảnh quan độc đ&aacute;o n&agrave;y, hay trở th&agrave;nh một phần nguy&ecirc;n nh&acirc;n ph&aacute; hủy n&oacute;?&rdquo;, tuy&ecirc;n bố đề cập th&ecirc;m.</p>\r\n\r\n<p>Leonel Arguello, Gi&aacute;m đốc T&agrave;i nguy&ecirc;n Thi&ecirc;n nhi&ecirc;n của C&ocirc;ng vi&ecirc;n Quốc gia Redwood, cho biết khu vực sinh trưởng của Hyperion hạn chế dịch vụ GPS v&agrave; s&oacute;ng điện thoại di động, đồng nghĩa rằng c&oacute; thể rất kh&oacute; khăn để giải cứu bất kỳ du kh&aacute;ch đi bộ đường d&agrave;i n&agrave;o bị lạc hoặc bị thương tại đ&acirc;y, theo&nbsp;<em>San Francisco Gate.</em></p>\r\n\r\n<p>Ngo&agrave;i x&oacute;i m&ograve;n v&agrave; hư hỏng ở phần gốc c&acirc;y, Hyperion gặp một số vấn đề thứ cấp đến từ con người.</p>\r\n\r\n<p>&ldquo;Kh&ocirc;ng chỉ vứt r&aacute;c bừa b&atilde;i, mọi người thậm ch&iacute; tạo ra nhiều lối m&ograve;n để đi vệ sinh. Họ cũng vứt lại giấy vệ sinh đ&atilde; qua sử dụng v&agrave; chất thải con người, điều n&agrave;y chẳng tốt đẹp g&igrave;&rdquo;, gi&aacute;m đốc Arguello n&oacute;i.</p>\r\n\r\n<p>Ngo&agrave;i ra, con người kh&ocirc;ng phải l&agrave; mối nguy duy nhất đối với những c&acirc;y khổng lồ n&agrave;y. Hiện ch&aacute;y rừng đang l&agrave; mối quan t&acirc;m ng&agrave;y c&agrave;ng gia tăng tr&ecirc;n khắp c&aacute;c c&ocirc;ng vi&ecirc;n quốc gia ở Mỹ.</p>\r\n\r\n<p>Năm 2021, ban l&atilde;nh đạo C&ocirc;ng vi&ecirc;n Quốc gia Sequoia v&agrave; Kings Canyon đ&atilde; thực hiện những biện ph&aacute;p nghi&ecirc;m khắc để bảo vệ một số c&acirc;y lớn nhất thế giới khỏi hỏa hoạn.</p>\r\n\r\n<p>Trong đ&oacute;, General Sherman, c&acirc;y lớn nhất theo khối lượng tr&ecirc;n thế giới, được bọc trong &ldquo;vật liệu chống lửa bằng nh&ocirc;m&rdquo; để giữ an to&agrave;n trong vụ ch&aacute;y rừng KNP Complex.</p>', 'Nguyễn Văn A', 0, 1, '22080392453220731233929_hyperion_tree.jpg', 'publish', '2022-08-03 02:24:53', NULL),
(12, 'Mặt trái của những ngôi nhà trên cây ở Indonesia', 'mat-trai-cua-nhung-ngoi-nha-tren-cay-o-indonesia', '<p>Một nh&agrave; s&aacute;ng tạo nội dung đ&atilde; chia sẻ trải nghiệm kh&ocirc;ng mấy tốt đẹp của m&igrave;nh tại nh&agrave; tr&ecirc;n c&acirc;y ở Nusa Penida.</p>\r\n\r\n<p>Nusa Penida l&agrave; một h&ograve;n đảo c&aacute;ch trung t&acirc;m Bali (Indonesia) khoảng 45 ph&uacute;t đi t&agrave;u. Điểm đến n&agrave;y nổi tiếng với những ng&ocirc;i nh&agrave; nằm tr&ecirc;n c&acirc;y rất ấn tượng.</p>\r\n\r\n<p>Nh&agrave; s&aacute;ng tạo nội dung Daiana đ&atilde; chia sẻ tr&ecirc;n trang c&aacute; nh&acirc;n của m&igrave;nh những h&igrave;nh ảnh thực tế về điểm lưu tr&uacute; n&agrave;y c&ugrave;ng d&ograve;ng trạng th&aacute;i &quot;Ugly truth&quot; (tạm dịch: sự thật phũ ph&agrave;ng).</p>\r\n\r\n<h3>Cơ sở vật chất tồi t&agrave;n</h3>\r\n\r\n<p>Tr&aacute;i với khung cảnh thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ b&ecirc;n ngo&agrave;i, nữ du kh&aacute;ch rất bất ngờ khi bước v&agrave;o b&ecirc;n trong. Ng&ocirc;i nh&agrave; được l&agrave;m bằng gỗ với những m&oacute;n đồ nội thất cơ bản như một chiếc nệm, khăn trải giường nh&agrave;u n&aacute;t, một c&aacute;i quạt v&agrave; b&agrave;n l&agrave;m việc.</p>\r\n\r\n<p>Diện t&iacute;ch của ng&ocirc;i nh&agrave; tr&ecirc;n c&acirc;y n&agrave;y cũng nhỏ hơn rất nhiều so với hạng ph&ograve;ng cơ bản trong kh&aacute;ch sạn hay resort. Ngo&agrave;i ra, trong ph&ograve;ng kh&ocirc;ng c&oacute; nh&agrave; vệ sinh. Du kh&aacute;ch sẽ sử dụng nh&agrave; vệ sinh chung ở b&ecirc;n dưới.</p>\r\n\r\n<p>Khu vực cầu thang đi l&ecirc;n nh&agrave; tr&ecirc;n c&acirc;y l&agrave; background quen thuộc n&ecirc;n c&oacute; kh&aacute; nhiều kh&aacute;ch du lịch đến check-in. Đ&acirc;y cũng l&agrave; điều khiến kh&aacute;ch thu&ecirc; cảm thấy kh&oacute; chịu.</p>\r\n\r\n<p>Bỏ qua chất lượng ph&ograve;ng ốc, theo&nbsp;<em>Wandering Wheatleys</em>, những căn nh&agrave; tr&ecirc;n c&acirc;y n&agrave;y lu&ocirc;n trong t&igrave;nh trạng k&iacute;n chỗ. Để c&oacute; thể trải nghiệm một đ&ecirc;m ở đ&acirc;y, bạn phải tốn khoảng&nbsp;40 USD&nbsp;v&agrave; đặt ph&ograve;ng trước &iacute;t nhất 3 th&aacute;ng.</p>\r\n\r\n<h3>Trả tiền cho view</h3>\r\n\r\n<p>Nhiều &yacute; kiến cho rằng khi đến đ&acirc;y, thứ du kh&aacute;ch trả tiền l&agrave; tầm nh&igrave;n hướng biển chứ kh&ocirc;ng phải chất lượng ph&ograve;ng ốc như c&aacute;c kh&aacute;ch sạn th&ocirc;ng thường.</p>\r\n\r\n<p>Đ&oacute;n b&igrave;nh minh hay ngắm ho&agrave;ng h&ocirc;n từ ban c&ocirc;ng ng&ocirc;i nh&agrave; tr&ecirc;n c&acirc;y sẽ l&agrave; một trải nghiệm m&agrave; bạn kh&ocirc;ng thể n&agrave;o qu&ecirc;n. Việc cơ sở vật chất kh&ocirc;ng qu&aacute; xuất sắc cũng l&agrave; một hạn chế thường gặp ở những điểm lưu tr&uacute; theo phong c&aacute;ch h&ograve;a m&igrave;nh với thi&ecirc;n nhi&ecirc;n v&agrave; xa trung t&acirc;m.</p>\r\n\r\n<p>Tr&ecirc;n trang c&aacute; nh&acirc;n của nh&agrave; s&aacute;ng tạo nội dung, một người d&ugrave;ng nhận x&eacute;t: &quot;T&ocirc;i kh&ocirc;ng mong đợi sự xuất hiện của một chiếc giường bằng b&ecirc; t&ocirc;ng hay mẫu tủ lạnh đời mới trong ng&ocirc;i nh&agrave; tr&ecirc;n c&acirc;y. Những thứ hiện c&oacute; l&agrave; lựa chọn ph&ugrave; hợp cho nơi ở h&ograve;a m&igrave;nh với thi&ecirc;n nhi&ecirc;n&quot;.</p>\r\n\r\n<p>Điểm lưu tr&uacute; n&agrave;y cũng nhận được những phản hồi t&iacute;ch cực về tầm nh&igrave;n đắt gi&aacute; m&agrave; du kh&aacute;ch sẽ được trải nghiệm khi ở đ&acirc;y. Một kh&aacute;ch du lịch chia sẻ: &quot;Thật kh&ocirc;ng thể tin được! Một nơi tuyệt vời để đ&oacute;n b&igrave;nh minh. Trong suốt thời gian lưu tr&uacute; sẽ lu&ocirc;n c&oacute; nh&acirc;n vi&ecirc;n hỗ trợ bạn chụp h&igrave;nh bất kỳ khung giờ n&agrave;o&quot;.</p>\r\n\r\n<h3>Một số lưu &yacute;</h3>\r\n\r\n<p>Khi quyết định đặt ph&ograve;ng ở những ng&ocirc;i nh&agrave; tr&ecirc;n c&acirc;y, đ&acirc;y l&agrave; một số điều bạn n&ecirc;n c&acirc;n nhắc, theo&nbsp;<em>Wandering Wheatleys</em>.</p>\r\n\r\n<ul>\r\n	<li><strong>Di chuyển:&nbsp;</strong>Mọi ng&ocirc;i nh&agrave; tr&ecirc;n c&acirc;y đều nằm tr&ecirc;n những v&aacute;ch n&uacute;i cao. Để l&ecirc;n đến nơi, bạn sẽ cần đi bộ một qu&atilde;ng đường kh&aacute; xa v&agrave; kh&ocirc;ng mấy đơn giản n&ecirc;n chỉ n&ecirc;n mang theo h&agrave;nh l&yacute; gọn nhẹ.</li>\r\n	<li><strong>Ph&ograve;ng ở:</strong>&nbsp;Ng&ocirc;i nh&agrave; được l&agrave;m bằng những tấm gỗ n&ecirc;n sẽ c&oacute; kh&aacute; nhiều khe hở. C&aacute;c lo&agrave;i c&ocirc;n tr&ugrave;ng c&oacute; thể th&ocirc;ng qua đ&oacute; để v&agrave;o trong ph&ograve;ng của bạn.</li>\r\n	<li><strong>Nh&agrave; vệ sinh:&nbsp;</strong>Buổi tối, việc di chuyển xuống nh&agrave; vệ sinh kh&aacute; kh&oacute; khăn. Nếu l&agrave; nữ, bạn c&oacute; thể trang bị một bộ dụng cụ ri&ecirc;ng hoặc mang theo đ&egrave;n pin.</li>\r\n</ul>', 'Nguyễn Văn A', 0, 2, '22080392651c129f39b6a67a839f176.jpg', 'publish', '2022-08-03 02:26:51', NULL),
(13, '3 món đậu đỏ cho ngày lễ Thất Tịch', '3-mon-dau-do-cho-ngay-le-that-tich', '<p>D&acirc;n gian tương truyền v&agrave;o ng&agrave;y lễ Thất Tịch (7/7 &acirc;m lịch), nếu bạn ăn đậu đỏ, sẽ sớm t&igrave;m được nửa c&ograve;n lại.</p>\r\n\r\n<p>Theo quan niệm của nhiều quốc gia, đậu đỏ vốn được xem l&agrave; nguy&ecirc;n liệu m&oacute;n ăn mang lại nhiều may mắn. M&agrave;u đỏ của đậu tượng trưng cho sự tốt l&agrave;nh v&agrave; hạnh ph&uacute;c.</p>\r\n\r\n<p>Cứ đến gần ng&agrave;y n&agrave;y, ăn đậu đỏ v&agrave;o ng&agrave;y lễ Thất Tịch trở th&agrave;nh n&eacute;t truyền thống đặc sắc. Theo truyền thuyết, nếu bạn c&ograve;n độc th&acirc;n th&igrave; sẽ nhanh ch&oacute;ng t&igrave;m được &yacute; trung nh&acirc;n. Nếu đ&atilde; c&oacute; đ&ocirc;i c&oacute; cặp, mối t&igrave;nh của bạn sẽ bền vững trọn đời.</p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; 3 m&oacute;n đậu đỏ dễ thực hiện tại nh&agrave;.</p>', 'Nguyễn Văn A', 0, 2, '22080392739pngtree_red_kidney_bean_photography_picture_image_1561737.jpeg', 'publish', '2022-08-03 02:27:39', NULL),
(14, 'Khó xử phạt nhóm vẽ bậy lên mỏm đá ở Quảng Ninh', 'kho-xu-phat-nhom-ve-bay-len-mom-da-o-quang-ninh', '<p>Th&ocirc;ng tin một nh&oacute;m người vẽ bậy l&ecirc;n mỏm đ&aacute; Chồng (x&atilde; Bằng Cả, Quảng Ninh) khiến cộng đồng mạng bức x&uacute;c. Tuy nhi&ecirc;n, việc xử phạt họ thế n&agrave;o vẫn c&ograve;n l&agrave; c&acirc;u hỏi.</p>\r\n\r\n<p>Tối 31/7, h&igrave;nh ảnh mỏm đ&aacute; Chồng bị vẽ bậy được lan truyền tr&ecirc;n mạng x&atilde; hội. Trong ảnh, một nh&oacute;m người mặc đồ đua xe đạp đ&atilde; sơn bậy l&ecirc;n mỏm đ&aacute; &quot;sống ảo&quot; nổi tiếng.</p>\r\n\r\n<p>Th&ocirc;ng tin ban đầu cho thấy đ&acirc;y l&agrave; những người trong một hội đam m&ecirc; xe đạp địa h&igrave;nh ở phường Mạo Kh&ecirc;, thị x&atilde; Đ&ocirc;ng Triều (Quảng Ninh).</p>\r\n\r\n<p>Trao đổi với&nbsp;<em>Zing</em>, &ocirc;ng Nguyễn Anh Dũng, Chủ tịch UBND x&atilde; Bằng Cả, cho biết đơn vị đ&atilde; nắm được th&ocirc;ng tin. Trong thời gian tới, họ sẽ tiến h&agrave;nh lau dọn vết bẩn được sơn tr&ecirc;n mỏm đ&aacute;. Tuy nhi&ecirc;n, việc xử phạt những người đ&atilde; vẽ bẩn l&ecirc;n mỏm đ&aacute; n&agrave;y kh&aacute; phức tạp.</p>\r\n\r\n<p>&quot;Vấn đề xử phạt nh&oacute;m người n&agrave;y kh&aacute; kh&oacute; n&oacute;i, bởi đ&acirc;y kh&ocirc;ng phải điểm du lịch được c&ocirc;ng nhận. N&oacute; chỉ l&agrave; mỏm đ&aacute; tự nhi&ecirc;n, c&oacute; h&igrave;nh dạng kỳ lạ n&ecirc;n được nhiều người đến tham quan&quot;, &ocirc;ng Dũng chia sẻ.</p>\r\n\r\n<p>Đ&aacute; Chồng l&agrave; mỏm đ&aacute; nổi tiếng ở Quảng Ninh. N&oacute; được tạo th&agrave;nh bởi h&agrave;ng chục phiến đ&aacute; xếp chồng l&ecirc;n nhau. Đứng từ tr&ecirc;n đỉnh, du kh&aacute;ch c&oacute; thể chi&ecirc;m ngưỡng cảnh quan của n&uacute;i, rừng Quảng Ninh.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, khu vực n&agrave;y c&oacute; địa h&igrave;nh kh&aacute; nguy hiểm. Do đ&oacute;, du kh&aacute;ch được khuyến c&aacute;o kh&ocirc;ng tới đ&acirc;y để tham quan, chụp ảnh.</p>\r\n\r\n<p>Trước đ&oacute;, UBND tỉnh Quảng Ninh từng ra văn bản y&ecirc;u cầu UBND x&atilde; Ho&agrave;nh Bồ (nay thuộc th&agrave;nh phố Hạ Long) cảnh b&aacute;o, ngăn chặn c&aacute;c hoạt động du lịch nguy hiểm tại khu vực mỏm đ&aacute; Chồng.</p>\r\n\r\n<p>Đồng thời, UBND tỉnh Quảng Ninh cũng y&ecirc;u cầu lập phương &aacute;n k&ecirc;u gọi đầu tư, ph&aacute;t triển sản phẩm du lịch tại đ&acirc;y v&agrave; b&aacute;o c&aacute;o cấp thẩm quyền c&ocirc;ng nhận l&agrave; điểm du lịch khi đủ điều kiện.</p>', 'Nguyễn Văn A', 0, 2, '22080392827z3608478351092_7f6e7e41b6833e436a8ed91afe535b49_2428.jpg', 'publish', '2022-08-03 02:28:27', NULL),
(15, 'Lượng khách quốc tế đến Việt Nam tăng 10 lần', 'luong-khach-quoc-te-den-viet-nam-tang-10-lan', '<p>So với c&ugrave;ng kỳ, lượng kh&aacute;ch quốc tế đến Việt Nam tăng mạnh nhưng vẫn thấp hơn năm 2019.</p>\r\n\r\n<p>Theo Tổng cục Thống k&ecirc;, 7 th&aacute;ng đầu năm, lượng kh&aacute;ch quốc tế đến Việt Nam đạt 352.600 lượt người, tăng 49% so với th&aacute;ng trước v&agrave; gấp 47,2 lần so với c&ugrave;ng kỳ năm 2021.</p>\r\n\r\n<p>T&iacute;nh chung 7 th&aacute;ng đầu năm, kh&aacute;ch quốc tế đến nước ta đạt 954.600 lượt, gấp 10 lần so với c&ugrave;ng kỳ năm trước nhưng vẫn giảm 90,3% so với c&ugrave;ng kỳ năm 2019, thời điểm chưa xảy ra dịch Covid-19.</p>\r\n\r\n<p>H&agrave;n Quốc l&agrave; nơi c&oacute; nhiều du kh&aacute;ch đến Việt Nam nhất với hơn 88.000 người. Xếp sau về lượt kh&aacute;ch l&agrave; Mỹ với hơn 34.000 người, Campuchia với hơn 24.000 người. C&aacute;c quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ kh&aacute;c c&oacute; lượng kh&aacute;ch du lịch đến Việt Nam nhiều lần lượt l&agrave; Nhật Bản, Th&aacute;i Lan, Australia, Singapore v&agrave; Malaysia.</p>\r\n\r\n<p>Lượt kh&aacute;ch quốc tế đến Việt Nam bằng đường h&agrave;ng kh&ocirc;ng tăng tới 1.251,4% với hơn 831.000 lượt kh&aacute;ch, di chuyển bằng đường biển chỉ c&oacute; 257 lượt nhưng vẫn tăng 8,9%.</p>\r\n\r\n<p>Tổng doanh thu du lịch lữ h&agrave;nh trong 7 th&aacute;ng đầu năm của cả nước ước t&iacute;nh đạt&nbsp;11,9 ngh&igrave;n tỷ đồng, gấp 2,7 lần so với c&ugrave;ng kỳ năm trước. Một số địa phương c&oacute; sự tăng trưởng trong doanh thu du lịch l&agrave; Kh&aacute;nh H&ograve;a (tăng 858,4%), Cần Thơ (tăng 328,3%), Đ&agrave; Nẵng (tăng 284,8%), H&agrave; Nội (tăng 216,8%) v&agrave; TP.HCM (tăng 111,4%).</p>\r\n\r\n<p>Cụ thể, tại Kh&aacute;nh H&ograve;a, trong 6 th&aacute;ng đầu năm, địa phương n&agrave;y đ&oacute;n hơn một triệu lượt kh&aacute;ch du lịch, doanh thu đạt gần&nbsp;5.530 tỷ đồng. C&ograve;n ở Đ&agrave; Nẵng, th&agrave;nh phố c&oacute; doanh thu du lịch xếp tr&ecirc;n H&agrave; Nội, Sở Du lịch tỉnh ước t&iacute;nh lượng kh&aacute;ch quốc tế đạt 57.800 lượt.</p>\r\n\r\n<p>C&oacute; thể thấy, c&aacute;c điểm du lịch biển ph&iacute;a nam nước ta thu h&uacute;t được sự quan t&acirc;m của du kh&aacute;ch trong nước cũng như quốc tế v&agrave; mang lại lợi nhuận cao.</p>\r\n\r\n<p>Trước đ&oacute;, trong một cuộc trao đổi với&nbsp;<em>Zing</em>, b&agrave; Ho&agrave;ng Tuyết, Gi&aacute;m đốc Top One Travel, cho biết t&igrave;nh h&igrave;nh dịch Covid-19 ở c&aacute;c v&ugrave;ng biển n&agrave;y cũng được đ&aacute;nh gi&aacute; t&iacute;ch cực hơn, l&agrave; điều kiện thuận lợi để ph&aacute;t triển du lịch.</p>', 'Nguyễn Văn A', 0, 2, '220803929424.jpg_zing.jpg', 'publish', '2022-08-03 02:29:42', NULL),
(16, '12 ngày rong ruổi Quy Nhơn - Phú Yên của gia đình trẻ', '12-ngay-rong-ruoi-quy-nhon-phu-yen-cua-gia-dinh-tre', '<p>Mặc d&ugrave; c&oacute; con nhỏ mới 10 th&aacute;ng tuổi, vợ chồng anh Trần Mạnh Huy vẫn c&ugrave;ng nhau vi vu hơn 1.000km đi chơi h&egrave; v&agrave; ghi lại những bức h&igrave;nh đẹp.</p>\r\n\r\n<p>L&agrave; một nhiếp ảnh gia đam m&ecirc; du lịch, Trương Mạnh Huy (H&agrave; Nội) kh&ocirc;ng qu&ecirc;n kể lại c&acirc;u chuyện bằng h&igrave;nh ảnh sau mỗi chuyến đi.</p>\r\n\r\n<p>Mỗi chặng đường của nhiếp ảnh gia 34 tuổi lu&ocirc;n c&oacute; c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh nhỏ đồng h&agrave;nh, l&agrave; vợ v&agrave; hai em b&eacute;. Bằng những h&igrave;nh ảnh chỉn chu, cảm x&uacute;c ghi được tr&ecirc;n đường, Mạnh Huy muốn chia sẻ g&oacute;c nh&igrave;n kh&aacute;c về việc kết h&ocirc;n của người trẻ.</p>\r\n\r\n<p>H&ocirc;n nh&acirc;n th&ecirc;m th&uacute; vị, m&agrave;u sắc khi biết c&ugrave;ng nhau trải nghiệm cuộc sống, Huy b&agrave;y tỏ.</p>\r\n\r\n<h3>Chuyến đi đ&oacute;n ch&agrave;o th&agrave;nh vi&ecirc;n mới</h3>\r\n\r\n<p>C&aacute;ch đ&acirc;y 3 năm, gia đ&igrave;nh anh Huy cũng từng g&acirc;y ch&uacute; &yacute; tr&ecirc;n mạng x&atilde; hội nhờ bộ ảnh rong ruổi một tuần ở Ninh Thuận. Khi đ&oacute;, nh&agrave; anh mới chỉ c&oacute; 3 th&agrave;nh vi&ecirc;n. Suốt hai năm dịch bệnh, gia đ&igrave;nh nhỏ bớt dần c&aacute;c chuyến đi, b&ugrave; đắp v&agrave;o đ&oacute; l&agrave; niềm vui ch&agrave;o đ&oacute;n th&agrave;nh vi&ecirc;n mới.</p>\r\n\r\n<p>Dịch bệnh dần qua đi, c&aacute;c th&agrave;nh vi&ecirc;n trong nh&agrave; lại rục rịch c&ugrave;ng nhau vi vu c&aacute;c cung đường đ&atilde; bỏ lỡ một thời gian d&agrave;i. T&igrave;m lại niềm đam m&ecirc; x&ecirc; dịch sau hơn 2 năm dịch bệnh, họ chọn điểm đến Quy Nhơn - Ph&uacute; Y&ecirc;n. Chuyến đi n&agrave;y đặc biệt với cả nh&agrave; Huy v&igrave; c&oacute; th&ecirc;m th&agrave;nh vi&ecirc;n mới tham gia.</p>\r\n\r\n<p>&quot;Từ b&acirc;y giờ, những chuyến đi của nh&agrave; m&igrave;nh kh&ocirc;ng chỉ c&oacute; 3 người nữa. Th&agrave;nh vi&ecirc;n thứ 4 được 10 th&aacute;ng tuổi, lần đầu ti&ecirc;n b&eacute; được du lịch c&ugrave;ng ba mẹ v&agrave; anh trai. H&agrave;nh tr&igrave;nh Quy Nhơn - Ph&uacute; Y&ecirc;n l&agrave; chuyến đi đầu ti&ecirc;n của b&eacute;, cũng l&agrave; chuyến đầu ti&ecirc;n 4 người đi c&ugrave;ng nhau&quot;, anh Huy chia sẻ.</p>\r\n\r\n<p>Nhiếp ảnh gia 8X chia sẻ anh lu&ocirc;n muốn chụp lại mọi khoảnh khắc của gia đ&igrave;nh m&igrave;nh. Những h&igrave;nh ảnh n&agrave;y l&agrave; m&oacute;n qu&agrave; anh d&agrave;nh tặng họ trong tương lai.</p>\r\n\r\n<p>&quot;Hy vọng khi c&aacute;c cậu b&eacute; của m&igrave;nh trưởng th&agrave;nh, những bức h&igrave;nh sẽ gi&uacute;p ch&uacute;ng c&oacute; một c&aacute;i nh&igrave;n t&iacute;ch cực hơn về gia đ&igrave;nh&quot;, Mạnh Huy b&agrave;y tỏ.</p>\r\n\r\n<p>Đằng sau những bức h&igrave;nh đẹp lung linh l&agrave; những gi&acirc;y ph&uacute;t &ocirc;ng bố 2 con mệt lả. Để c&oacute; một bức ảnh ưng &yacute;, Huy phải tự chuẩn bị mọi thứ, cắm ch&acirc;n m&aacute;y, hẹn giờ, căn g&oacute;c chụp, chạy đi chạy lại bấm m&aacute;y.</p>\r\n\r\n<p>&quot;Cực nhất l&agrave; nhiều l&uacute;c phải dỗ d&agrave;nh 2 em b&eacute; ăn uống, đi vệ sinh ch&aacute;n ch&ecirc; mới chịu tạo d&aacute;ng. Thật ra m&igrave;nh chủ yếu chụp những khoảnh khắc tự nhi&ecirc;n khi cả gia đ&igrave;nh chơi th&ocirc;i, chứ bắt c&aacute;c b&eacute; tạo d&aacute;ng th&igrave; rất kh&oacute;&quot;, nhiếp ảnh gia 8X chia sẻ.</p>\r\n\r\n<p>N&oacute;i mệt mỏi l&agrave; vậy nhưng Huy lu&ocirc;n cảm thấy vui khi nh&igrave;n lại từng khoảnh khắc hạnh ph&uacute;c của gia đ&igrave;nh trong từng khung h&igrave;nh. &Aacute;nh mắt hồn nhi&ecirc;n, nụ cười trong veo của c&aacute;c con như &quot;liều doping&quot; khiến &ocirc;ng bố 2 con qu&ecirc;n hết mọi mệt mỏi.</p>\r\n\r\n<h3>12 ng&agrave;y ở Quy Nhơn - Ph&uacute; Y&ecirc;n</h3>\r\n\r\n<p>Mạnh Huy cho biết cả nh&agrave; d&agrave;nh 12 ng&agrave;y để kh&aacute;m ph&aacute; Quy Nhơn - Ph&uacute; Y&ecirc;n. Gia đ&igrave;nh 4 người chọn đi chơi d&agrave;i ng&agrave;y v&igrave; kh&ocirc;ng muốn 2 em b&eacute; phải di chuyển cập rập li&ecirc;n tục, dễ mệt.</p>\r\n\r\n<p>Hơn nữa, vợ chồng Huy đều l&agrave;m những c&ocirc;ng việc linh động thời gian n&ecirc;n dễ sắp xếp một chuyến đi chơi d&agrave;i m&agrave; kh&ocirc;ng bị ảnh hưởng.</p>\r\n\r\n<p>Lịch tr&igrave;nh chuyến kh&aacute;m ph&aacute; v&ugrave;ng biển duy&ecirc;n hải miền Trung gồm 6 ng&agrave;y đầu ở Quy Nhơn, thời gian c&ograve;n lại ở Ph&uacute; Y&ecirc;n.</p>\r\n\r\n<p>Để thuận tiện di chuyển, tham quan c&aacute;c điểm, gia đ&igrave;nh Huy chạy xe m&aacute;y suốt h&agrave;nh tr&igrave;nh. Tại Quy Nhơn, 4 người d&agrave;nh thời gian check-in ở Eo Gi&oacute;, Kỳ Co, H&ograve;n Kh&ocirc;, l&agrave;ng Phong Quy H&ograve;a.</p>\r\n\r\n<p>Từ Quy Nhơn, cả nh&agrave; tiếp tục di chuyển đến Ph&uacute; Y&ecirc;n bằng t&agrave;u hỏa. &quot;Ở xứ Nẫu, nh&agrave; m&igrave;nh cũng chạy xe m&aacute;y qua c&aacute;c điểm như Mũi Điện, bờ k&egrave; X&oacute;m Rớ, hải đăng G&agrave;nh Đ&egrave;n, n&uacute;i &Ocirc;ng, b&atilde;i M&ocirc;n, b&atilde;i X&eacute;p&quot;, Huy chia sẻ.</p>\r\n\r\n<p>&Ocirc;ng bố 2 con cho biết điều kh&oacute; khăn nhất khi du lịch c&ugrave;ng trẻ nhỏ l&agrave; phải mang v&aacute;c nhiều đồ lỉnh kỉnh như bỉm sữa, đồ chơi, đồ ăn. C&aacute;c b&eacute; sẽ cảm thấy kh&oacute; chịu khi phải mặc nhiều quần &aacute;o, đội mũ, đeo khẩu trang trong một chặng đường d&agrave;i.</p>\r\n\r\n<p>&quot;Mỗi khi kh&oacute; chịu c&aacute;c b&eacute; sẽ quấy kh&oacute;c v&agrave; đ&ograve;i về. V&igrave; thế nh&agrave; m&igrave;nh tranh thủ đi chơi, s&aacute;ng 3 tiếng, chiều 3 tiếng v&agrave; chọn đi v&agrave;o những l&uacute;c trời m&aacute;t mẻ&quot;, Mạnh Huy kể lại.</p>\r\n\r\n<p>V&igrave; c&oacute; nhiều kinh nghiệm du lịch từ những chuyến đi trước với em b&eacute; lớn, n&ecirc;n chuyến đi c&oacute; th&ecirc;m th&agrave;nh vi&ecirc;n nh&iacute; 10 th&aacute;ng tuổi kh&ocirc;ng qu&aacute; kh&oacute; khăn cho Huy v&agrave; vợ. Anh cho biết phần lớn thời gian di chuyển tr&ecirc;n xe c&aacute;c b&eacute; sẽ ngủ, khi đến c&aacute;c điểm du lịch c&aacute;c b&eacute; sẽ dậy chơi. Tr&ecirc;n đường trở về kh&aacute;ch sạn c&aacute;c b&eacute; cũng tranh thủ ngủ để lấy sức.</p>\r\n\r\n<p>&quot;C&aacute;c b&eacute; c&oacute; l&uacute;c kh&oacute;c, quấy nhưng cũng c&oacute; nhiều l&uacute;c đ&aacute;ng y&ecirc;u. Khi về nh&agrave;, m&igrave;nh thường &iacute;t nhớ về cảnh đẹp hay những l&uacute;c vui. Kỷ niệm đ&aacute;ng nhớ nhất l&agrave; khoảnh khắc cả nh&agrave; đồng l&ograve;ng vượt qua kh&oacute; khăn c&ugrave;ng nhau&quot;, Huy b&agrave;y tỏ.</p>', 'Nguyễn Văn A', 0, 2, '22080393132DSC_4677.jpg', 'publish', '2022-08-03 02:31:32', NULL),
(17, 'Voi trắng quý hiếm xuất hiện ở Myanmar', 'voi-trang-quy-hiem-xuat-hien-o-myanmmar', '<p>Truyền th&ocirc;ng Myanmar ng&agrave;y 3/8 cho biết một con voi trắng qu&yacute; hiếm đ&atilde; ch&agrave;o đời ở miền T&acirc;y nước n&agrave;y.</p>\r\n\r\n<p>Được sinh ra v&agrave;o th&aacute;ng 7 ở bang miền T&acirc;y Rakhine, voi con nặng khoảng 80 kg v&agrave; cao khoảng 70 cm, theo&nbsp;<em>Global New Light.</em></p>\r\n\r\n<p>Những thước phim do k&ecirc;nh truyền h&igrave;nh nh&agrave; nước Myanmar cho thấy voi con theo mẹ đến một d&ograve;ng s&ocirc;ng v&agrave; được những người tr&ocirc;ng giữ tắm rửa trước khi được cho ăn.</p>\r\n\r\n<p>Voi mẹ 33 tuổi, t&ecirc;n l&agrave; Zar Nan Hla, đang được nu&ocirc;i giữ ở Myanma Timber Enterprise tại bang Rakhine,&nbsp;<em>Global New Light&nbsp;</em>cho biết v&agrave; n&oacute;i th&ecirc;m voi con sở hữu 7 trong số 8 đặc điểm li&ecirc;n quan đến voi trắng qu&yacute; hiếm.</p>\r\n\r\n<p>&ldquo;Đ&ocirc;i mắt m&agrave;u ngọc trai, lưng h&igrave;nh c&agrave;nh c&acirc;y, l&ocirc;ng trắng, đu&ocirc;i đặc biệt, c&oacute; những dấu hiệu điềm l&agrave;nh tr&ecirc;n da, 5 m&oacute;ng vuốt ở ch&acirc;n trước v&agrave; 4 m&oacute;ng ở ch&acirc;n sau c&ugrave;ng đ&ocirc;i tai lớn&rdquo;, tờ b&aacute;o mi&ecirc;u tả.</p>\r\n\r\n<p>Người d&ugrave;ng mạng x&atilde; hội lần đầu ti&ecirc;n đăng tải th&ocirc;ng tin về sự ra đời của ch&uacute; voi con - hiện vẫn chưa được đặt t&ecirc;n - v&agrave;o cuối th&aacute;ng trước. Một số đ&atilde; b&agrave;y tỏ th&aacute;i độ ho&agrave;i nghi.</p>\r\n\r\n<p>&quot;T&ocirc;i c&oacute; bị m&ugrave; m&agrave;u kh&ocirc;ng khi t&ocirc;i thấy n&oacute; m&agrave;u n&acirc;u?&quot;, một người d&ugrave;ng chia sẻ.</p>\r\n\r\n<p>&quot;Voi chỉ quan trọng trong thời đại cũ m&agrave; th&ocirc;i&quot;, một người kh&aacute;c n&oacute;i.</p>\r\n\r\n<p>Người d&acirc;n Myanmar tin rằng những ch&uacute; voi trắng, m&agrave; tr&ecirc;n thực tế l&agrave; da của ch&uacute;ng c&oacute; m&agrave;u n&acirc;u v&agrave; khi xuống nước th&igrave; c&oacute; m&agrave;u hồng nhạt, c&oacute; thể đem lại nhiều điều may mắn cho đất nước cũng như cho mọi người d&acirc;n.</p>\r\n\r\n<p>Voi trắng được t&ocirc;n k&iacute;nh như một lo&agrave;i vật linh thi&ecirc;ng trong t&iacute;n ngưỡng Phật gi&aacute;o của người Myanmar, Th&aacute;i Lan v&agrave; L&agrave;o từ nhiều thế kỷ nay. Những ch&uacute; voi trắng thường được nu&ocirc;i như th&uacute; cưng của ho&agrave;ng gia v&agrave; l&agrave; một biểu tượng ki&ecirc;u h&atilde;nh của quyền lực v&agrave; sự gi&agrave;u c&oacute;.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, sự tốn k&eacute;m trong việc nu&ocirc;i giữ voi trắng theo phong c&aacute;ch xa hoa đ&atilde; khiến nhiều người thay đổi th&aacute;i độ, cảm thấy voi trắng kh&ocirc;ng phải lao động lại ăn nhiều l&agrave; g&aacute;nh nặng, l&agrave;m ti&ecirc;u t&aacute;n t&agrave;i sản của gia đ&igrave;nh người nu&ocirc;i.</p>\r\n\r\n<p>Hiện c&oacute; 6 con voi trắng đang được nu&ocirc;i giữ ở thủ đ&ocirc; Naypyidaw, theo truyền th&ocirc;ng nh&agrave; nước. Chủ yếu ch&uacute;ng đến từ bang Rakhine v&agrave; v&ugrave;ng ph&iacute;a nam Ayeyarwady.</p>', 'Nguyễn Văn A', 0, 3, '22080393345000_32FW49E.jpg', 'publish', '2022-08-03 02:33:45', NULL),
(18, 'Trung Quốc ngừng nhập khẩu cam Đài Loan', 'tq-ngung-nhap-khau-cam-dai-loan', '<p>Trung Quốc ng&agrave;y 3/8 tuy&ecirc;n bố ngừng nhập khẩu c&aacute;c loại tr&aacute;i c&acirc;y thuộc họ cam chanh, cũng như c&aacute; hố, c&aacute; s&ograve;ng đ&ocirc;ng lạnh từ Đ&agrave;i Loan, đồng thời cấm xuất khẩu c&aacute;t tự nhi&ecirc;n sang h&ograve;n đảo.</p>\r\n\r\n<p>Theo ph&iacute;a Trung Quốc, họ đ&atilde; li&ecirc;n tục ph&aacute;t hiện virus g&acirc;y Covid-19 tr&ecirc;n bao b&igrave; hai sản phẩm c&aacute; tr&ecirc;n. H&atilde;ng th&ocirc;ng tấn&nbsp;<em>CNA&nbsp;</em>của Đ&agrave;i Loan cho biết hai loại c&aacute; n&agrave;y từng bị cấm nhập khẩu một tuần hồi th&aacute;ng 6 với l&yacute; do tương tự.</p>\r\n\r\n<p>Trong khi đ&oacute;, nguy&ecirc;n nh&acirc;n cấm nhập khẩu c&aacute;c loại tr&aacute;i c&acirc;y thuộc họ cam chanh được Bắc Kinh đưa ra l&agrave; việc c&aacute;c sản phẩm n&agrave;y chứa s&acirc;u hại v&agrave; dư lượng hai loại thuốc bảo vệ thực vật vượt ngưỡng.</p>\r\n\r\n<p>Trước đ&oacute; c&ugrave;ng ng&agrave;y, Bộ Thương mại Trung Quốc cũng tuy&ecirc;n bố nước n&agrave;y sẽ tạm ngừng xuất khẩu c&aacute;t tự nhi&ecirc;n tới Đ&agrave;i Loan, bắt đầu từ ng&agrave;y 3/8.</p>\r\n\r\n<p>C&aacute;c động th&aacute;i tr&ecirc;n được đưa ra chỉ v&agrave;i giờ sau khi Chủ tịch Hạ viện Mỹ Nancy Pelosi tới Đ&agrave;i Loan, bắt đầu chuyến thăm g&acirc;y tranh c&atilde;i tới v&ugrave;ng l&atilde;nh thổ n&agrave;y. Đ&acirc;y l&agrave; chuyến thăm cấp cao nhất của một quan chức Mỹ trong 25 năm, kể từ sau chuyến c&ocirc;ng du của cựu Chủ tịch Hạ viện Mỹ Newt Gingrich năm 1997.</p>\r\n\r\n<p>Chuyến thăm đ&atilde; vấp phải phản ứng mạnh mẽ từ Trung Quốc. Bộ Ngoại giao nước n&agrave;y tuy&ecirc;n bố đ&acirc;y l&agrave; &ldquo;h&agrave;nh động ch&iacute;nh trị nghi&ecirc;m trọng&rdquo;, trong khi Bộ Quốc ph&ograve;ng Trung Quốc th&ocirc;ng b&aacute;o sẽ tập trận bắn đạn thật tại 6 v&ugrave;ng biển bao quanh đảo Đ&agrave;i Loan.</p>\r\n\r\n<p>Trước đ&oacute;, tối 1/8, hải quan Trung Quốc cũng đ&atilde; tạm cấm nhập khẩu hơn 100 mặt h&agrave;ng thực phẩm chế biến của Đ&agrave;i Loan, bao gồm nhiều loại b&aacute;nh kẹo, b&aacute;nh quy, b&aacute;nh ngọt v&agrave; b&aacute;nh m&igrave;,&nbsp;<em>CNA</em>&nbsp;cho biết.</p>', 'Từ Quốc Minh', 0, 3, '2208039372838_tq_dl_reuters.jpg', 'publish', '2022-08-03 02:37:28', NULL),
(19, 'Bà Thái Anh Văn muốn duy trì nguyên trạng eo biển Đài Loan', 'ba-thai-anh-van-muon-duy-tri-nguyen-trang-eo-bien-dai-loan', '<p>Nh&agrave; l&atilde;nh đạo Đ&agrave;i Loan Th&aacute;i Anh Văn ng&agrave;y 3/8 tuy&ecirc;n bố v&ugrave;ng l&atilde;nh thổ n&agrave;y muốn duy tr&igrave; nguy&ecirc;n trạng t&igrave;nh h&igrave;nh ở eo biển Đ&agrave;i Loan, theo Guardian.</p>\r\n\r\n<p>Tuy&ecirc;n bố tr&ecirc;n được b&agrave; Th&aacute;i đưa ra trong cuộc họp b&aacute;o chung với Chủ tịch Hạ viện Mỹ Nancy Pelosi tại Đ&agrave;i Bắc.</p>\r\n\r\n<p>&ldquo;Người Đ&agrave;i Loan rất thực tế&rdquo;, b&agrave; bổ sung.</p>\r\n\r\n<p>Người đứng đầu ch&iacute;nh quyền Đ&agrave;i Loan cũng nhận định c&aacute;c cuộc diễn tập qu&acirc;n sự của Trung Quốc đại lục l&agrave; &ldquo;phản ứng kh&ocirc;ng cần thiết&rdquo;. &ldquo;Đ&agrave;i Loan lu&ocirc;n sẵn s&agrave;ng đối thoại một c&aacute;ch x&acirc;y dựng. Ch&uacute;ng t&ocirc;i sẽ l&agrave;m việc với c&aacute;c b&ecirc;n để mang lại sự ổn định v&agrave; h&ograve;a b&igrave;nh trong khu vực&rdquo;, b&agrave; n&oacute;i.</p>\r\n\r\n<p>Về phần m&igrave;nh, b&agrave; Pelosi tuy&ecirc;n bố Mỹ mong muốn Đ&agrave;i Loan c&oacute; &ldquo;sự tự do đi k&egrave;m với an ninh&rdquo;.</p>\r\n\r\n<p>&ldquo;Trong khi t&ocirc;n trọng ch&iacute;nh s&aacute;ch &lsquo;Một Trung Quốc&rsquo; của m&igrave;nh, sự đo&agrave;n kết của ch&uacute;ng t&ocirc;i với Đ&agrave;i Loan l&agrave; quan trọng hơn bao giờ hết&rdquo;, b&agrave; n&oacute;i. &ldquo;Ch&uacute;ng t&ocirc;i mong Đ&agrave;i Loan lu&ocirc;n c&oacute; sự tự do đi k&egrave;m an ninh, ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng l&ugrave;i bước về điều n&agrave;y&rdquo;.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, b&agrave; Pelosi cũng khẳng định Mỹ ủng hộ duy tr&igrave; nguy&ecirc;n trạng t&igrave;nh h&igrave;nh. &ldquo;Ch&uacute;ng t&ocirc;i kh&ocirc;ng muốn điều g&igrave; xảy ra với Đ&agrave;i Loan bằng vũ lực&rdquo;, b&agrave; cho biết.</p>\r\n\r\n<p>Khi được hỏi về c&aacute;c t&aacute;c động về kinh tế m&agrave; Đ&agrave;i Loan phải đối mặt sau chuyến thăm, b&agrave; Pelosi cho biết đạo luật về sản phẩm b&aacute;n dẫn (đạo luật CHIPS) của Mỹ mở ra th&ecirc;m c&aacute;nh cửa để hai b&ecirc;n trao đổi kinh tế.</p>\r\n\r\n<p>Trước đ&oacute;, b&agrave; Pelosi đ&atilde; được b&agrave; Th&aacute;i trao tặng hu&acirc;n chương &ldquo;Khanh V&acirc;n&rdquo; hạng nhất v&igrave; c&aacute;c đ&oacute;ng g&oacute;p trong th&uacute;c đẩy quan hệ giữa Đ&agrave;i Loan v&agrave; Mỹ. &ldquo;T&ocirc;i hy vọng c&oacute; thể trưng b&agrave;y hu&acirc;n chương n&agrave;y trong văn ph&ograve;ng của chủ tịch Hạ viện, hoặc đeo ch&uacute;ng trong Điện Capitol như một biểu tượng của t&igrave;nh hữu nghị qu&yacute; b&aacute;u giữa ch&uacute;ng ta&rdquo;, vị chủ tịch Hạ viện Mỹ n&oacute;i về phần thưởng n&agrave;y.</p>', 'Từ Quốc Minh', 0, 3, '22080393825thai_anh_van_reuters_1.jpg', 'publish', '2022-08-03 02:38:25', NULL),
(20, 'Anh tiếp tục công nhận hộ chiếu mới của Việt Nam', 'anh-tiep-tuc-cong-nhan-ho-chieu-moi-cua-viet-nam', '<p>S&aacute;ng 3/8, Đại sứ qu&aacute;n Anh tại H&agrave; Nội cho biết Vương quốc Anh tiếp tục c&ocirc;ng nhận mẫu hộ chiếu mới của Việt Nam.</p>\r\n\r\n<p>Đại sứ qu&aacute;n Anh cũng lưu &yacute; người nộp đơn xin thị thực v&agrave; du kh&aacute;ch (bao gồm c&aacute;c trường hợp qu&aacute; cảnh) cần cập nhật v&agrave; kiểm tra th&ocirc;ng tin thường xuy&ecirc;n về c&aacute;c quy định li&ecirc;n quan tới hộ chiếu cho những điểm đến kh&aacute;c trong h&agrave;nh tr&igrave;nh của m&igrave;nh.</p>\r\n\r\n<p>Trước đ&oacute;, tương tự Anh, Đại sứ qu&aacute;n Ph&aacute;p h&ocirc;m 28/7 th&ocirc;ng b&aacute;o Ph&aacute;p sẽ vẫn tiếp tục c&ocirc;ng nhận hộ chiếu mới của Việt Nam v&agrave; cấp thị thực b&igrave;nh thường cho tới khi c&oacute; th&ocirc;ng b&aacute;o mới.</p>\r\n\r\n<p>Trong khi đ&oacute;, Cộng h&ograve;a Czech, Đức v&agrave; T&acirc;y Ban Nha đ&atilde; th&ocirc;ng b&aacute;o kh&ocirc;ng c&ocirc;ng nhận mẫu hộ chiếu mới b&igrave;a xanh t&iacute;m than của Việt Nam được bắt đầu cấp từ ng&agrave;y 1/7.</p>\r\n\r\n<p>Đại sứ qu&aacute;n Đức ra th&ocirc;ng b&aacute;o từ ng&agrave;y 27/7, trong khi Đại sứ qu&aacute;n T&acirc;y Ban Nha v&agrave; Cộng h&ograve;a Czech c&oacute; tuy&ecirc;n bố tương tự lần lượt v&agrave;o ng&agrave;y 1/8 v&agrave; 2/8.</p>\r\n\r\n<p>L&yacute; do được c&aacute;c đại sứ qu&aacute;n n&agrave;y đưa ra c&ugrave;ng l&agrave; v&igrave; mẫu hộ chiếu mới kh&ocirc;ng c&oacute; th&ocirc;ng tin về nơi sinh, g&acirc;y kh&oacute; khăn khi tra cứu v&agrave; x&aacute;c minh danh t&iacute;nh.</p>\r\n\r\n<p>Trong bối cảnh đ&oacute;, để tạo thuận lợi cho c&ocirc;ng d&acirc;n Việt Nam, Đại sứ qu&aacute;n Việt Nam tại Cộng h&ograve;a Czech đ&atilde; th&ocirc;ng b&aacute;o cấp hộ chiếu mới v&agrave; ghi bị ch&uacute; &ldquo;nơi sinh&rdquo; v&agrave;o trang 4 của hộ chiếu mẫu mới.</p>\r\n\r\n<p>Từ ng&agrave;y 1/7, Cục Quản l&yacute; Xuất nhập cảnh bắt đầu triển khai cấp hộ chiếu phổ th&ocirc;ng kh&ocirc;ng gắn chip theo mẫu mới cho c&ocirc;ng d&acirc;n. Mẫu hộ chiếu mới đ&aacute;p ứng y&ecirc;u cầu bảo an cao hơn, chống nguy cơ l&agrave;m giả, đạt ti&ecirc;u chuẩn ICAO (Tổ chức H&agrave;ng kh&ocirc;ng d&acirc;n dụng quốc tế).</p>\r\n\r\n<p>Hộ chiếu mẫu mới gồm 50 trang. Trong đ&oacute; c&oacute; 2 trang b&igrave;a m&agrave;u t&iacute;m than, được in tr&ecirc;n loại chất liệu giấy mới d&agrave;y hơn so với loại hộ chiếu cũ.</p>\r\n\r\n<p>Sau khi c&oacute; th&ocirc;ng tin hộ chiếu mới chưa được một số nước c&ocirc;ng nhận, tối 27/7, đại diện Cục Quản l&yacute; Xuất nhập cảnh, Bộ C&ocirc;ng an khẳng định hộ chiếu phổ th&ocirc;ng mẫu mới của Việt Nam được x&acirc;y dựng tr&ecirc;n cơ sở đ&uacute;ng luật, đ&uacute;ng quy định v&agrave; đ&uacute;ng quy chuẩn quốc tế.</p>', 'Từ Quốc Minh', 0, 3, '22080393914ho_chieu_moi.jpg', 'publish', '2022-08-03 02:39:14', NULL),
(21, 'Đài Loan tuyên bố sẽ \'bảo vệ an ninh\' hòn đảo', 'dai-loan-tuyen-bo-se-bao-ve-an-ninh-hon-dao', '<p>Cơ quan ph&ograve;ng vệ Đ&agrave;i Loan ng&agrave;y 3/8 tuy&ecirc;n bố sẽ ki&ecirc;n quyết bảo vệ an ninh h&ograve;n đảo giữa l&uacute;c Trung Quốc ph&aacute;t động cuộc tập trận ở 6 địa điểm xung quanh v&ugrave;ng l&atilde;nh thổ n&agrave;y.</p>\r\n\r\n<p>Trong cuộc họp b&aacute;o ng&agrave;y 3/8, cơ quan ph&ograve;ng vệ Đ&agrave;i Loan cho rằng Trung Quốc &ldquo;tiếp tục ph&aacute;t động chiến tranh t&acirc;m l&yacute; nhằm v&agrave;o Đ&agrave;i Loan v&agrave; d&acirc;n thường&quot;.</p>\r\n\r\n<p>Cơ quan n&agrave;y n&oacute;i c&aacute;c cuộc tập trận của Trung Quốc đ&atilde; &ldquo;x&acirc;m phạm kh&ocirc;ng gian&quot; của đảo n&agrave;y, đồng thời c&aacute;o buộc c&aacute;c cuộc tập trận &ldquo;vi phạm c&aacute;c quy tắc của Li&ecirc;n Hợp Quốc&rdquo;,&nbsp;<em>Guardian</em>&nbsp;đưa tin.</p>\r\n\r\n<p>Cơ quan n&agrave;y n&oacute;i th&ecirc;m c&aacute;c cuộc tập trận của Trung Quốc &ldquo;c&oacute; thể phong tỏa kh&ocirc;ng gian tr&ecirc;n kh&ocirc;ng v&agrave; tr&ecirc;n biển của Đ&agrave;i Loan&rdquo;. Đ&agrave;i Loan sẽ n&acirc;ng cao mức độ cảnh gi&aacute;c &ldquo;theo nguy&ecirc;n tắc kh&ocirc;ng k&iacute;ch động một cuộc chiến&rdquo;.</p>\r\n\r\n<p>Trong khi đ&oacute;, cơ quan ngoại giao Đ&agrave;i Loan n&oacute;i rằng &ldquo;động th&aacute;i leo thang c&oacute; chủ &yacute;&rdquo; của c&aacute;c h&agrave;nh động qu&acirc;n sự chống lại h&ograve;n đảo sẽ t&aacute;c động đến trao đổi kinh tế v&agrave; thương mại quốc tế, đồng thời &quot;ph&aacute; hoại&quot; h&ograve;a b&igrave;nh v&agrave; ổn định trong khu vực.</p>\r\n\r\n<p>Trước đ&oacute;, ng&agrave;y 2/8,&nbsp;<em>Taipei Times&nbsp;</em>dẫn nguồn tin cho biết cơ quan ph&ograve;ng vệ Đ&agrave;i Loan đ&atilde; tăng cường cảnh b&aacute;o chiến đấu để đối ph&oacute; với c&aacute;c cuộc tập trận bắn đạn thật của Trung Quốc. Tuy nhi&ecirc;n, nguồn tin n&oacute;i th&ecirc;m rằng khả năng sẵn s&agrave;ng chiến đấu vẫn ở &ldquo;mức độ b&igrave;nh thường&rdquo; theo hệ thống hai cấp của h&ograve;n đảo, v&agrave; chưa được n&acirc;ng l&ecirc;n th&agrave;nh &ldquo;mức độ sẵn s&agrave;ng khẩn cấp&rdquo;.</p>\r\n\r\n<p>Trung Quốc đ&atilde; tuy&ecirc;n bố tập trận đạn thật tr&ecirc;n 6 v&ugrave;ng biển, tạo th&agrave;nh thế bao v&acirc;y đảo Đ&agrave;i Loan, để đ&aacute;p trả chuyến thăm của Chủ tịch Hạ viện Mỹ Nancy Pelosi đến h&ograve;n đảo.</p>\r\n\r\n<p>Theo hải đồ, 6 v&ugrave;ng biển n&agrave;y ph&acirc;n bố quanh h&ograve;n đảo, ở ph&iacute;a Bắc, Đ&ocirc;ng Bắc, T&acirc;y Bắc, Đ&ocirc;ng, Nam v&agrave; T&acirc;y Nam của Đ&agrave;i Loan, đồng thời nằm trong v&ugrave;ng thuộc c&aacute;c eo biển Đ&agrave;i Loan, eo biển Ba Sĩ, biển Hoa Đ&ocirc;ng, v&agrave; khu vực ngo&agrave;i khơi th&agrave;nh phố Cao H&ugrave;ng c&aacute;ch đất liền chưa đầy 20 km. Cao H&ugrave;ng l&agrave; trung t&acirc;m chế tạo, lọc dầu v&agrave; vận tải lớn của Đ&agrave;i Loan,&nbsp;<em>Apple Daily</em>&nbsp;cho biết.</p>\r\n\r\n<p>Bộ Ngoại giao Trung Quốc tuy&ecirc;n bố chuyến của b&agrave; Pelosi n&agrave;y l&agrave; &quot;h&agrave;nh động khi&ecirc;u kh&iacute;ch ch&iacute;nh trị nghi&ecirc;m trọng&quot; v&agrave; &quot;ki&ecirc;n quyết phản đối&quot;.</p>\r\n\r\n<p>Chiến khu Trung bộ của qu&acirc;n đội Trung Quốc tuy&ecirc;n bố sẽ tiến h&agrave;nh một loạt &ldquo;h&agrave;nh động qu&acirc;n sự li&ecirc;n hợp&rdquo; quanh đảo Đ&agrave;i Loan từ tối 2/8, với sự tham gia của cả hải qu&acirc;n v&agrave; kh&ocirc;ng qu&acirc;n ở v&ugrave;ng biển ph&iacute;a bắc, ph&iacute;a t&acirc;y nam v&agrave; ph&iacute;a đ&ocirc;ng nam đảo Đ&agrave;i Loan.</p>\r\n\r\n<p>Lực lượng n&agrave;y cũng tuy&ecirc;n bố sẽ ph&oacute;ng thử vũ kh&iacute; tầm xa ở eo biển Đ&agrave;i Loan, cũng như tập trận bắn đạn thật ở v&ugrave;ng biển ph&iacute;a đ&ocirc;ng Đ&agrave;i Loan. C&aacute;c h&agrave;nh động n&agrave;y nhằm đ&aacute;p trả động th&aacute;i của Mỹ trong vấn đề Đ&agrave;i Loan,&nbsp;<em>Reuters&nbsp;</em>cho biết.</p>', 'Từ Quốc Minh', 0, 3, '22080394033trung_quoc_tap_tran_quanh_dai_loan.jpeg', 'publish', '2022-08-03 02:40:33', NULL),
(22, 'Thiên hà Bánh xe dưới góc nhìn của kính viễn vọng 10 tỷ USD', 'thien-ha-banh-xe-duoi-goc-nhin-cua-kinh-vien-vong-10-ty-usd', '<p>Nhờ dữ liệu từ k&iacute;nh viễn vọng James Webb, c&aacute;c nh&agrave; khoa học đ&atilde; tạo ra h&igrave;nh ảnh chi tiết của Cartwheel, thi&ecirc;n h&agrave; kỳ lạ với kết cấu rất phức tạp.</p>\r\n\r\n<p>Ng&agrave;y 2/8, Cơ quan H&agrave;ng kh&ocirc;ng Vũ trụ Mỹ (NASA) chia sẻ h&igrave;nh ảnh thi&ecirc;n h&agrave; Cartwheel (Thi&ecirc;n h&agrave; B&aacute;nh xe) chụp bởi k&iacute;nh viễn vọng James Webb.</p>\r\n\r\n<p>C&aacute;ch ch&uacute;ng ta khoảng 500 triệu năm &aacute;nh s&aacute;ng, Cartwheel được xem l&agrave; một trong những thi&ecirc;n h&agrave; kỳ lạ, nhiều chi tiết bị bụi che khuất. Nhờ c&aacute;c c&ocirc;ng cụ hiện đại, James Webb c&oacute; thể ph&acirc;n t&iacute;ch một số th&agrave;nh phần của thi&ecirc;n h&agrave; n&agrave;y.</p>\r\n\r\n<p>Đ&uacute;ng như biệt danh &quot;B&aacute;nh xe&quot;, Cartwheel c&oacute; h&igrave;nh dạng giống b&aacute;nh xe ngựa, l&agrave; kết quả sau vụ va chạm dữ dội giữa thi&ecirc;n h&agrave; xoắn ốc với một thi&ecirc;n h&agrave; nhỏ hơn. Điều đ&oacute; g&acirc;y ra c&aacute;c đợt s&oacute;ng xung k&iacute;ch với tốc độ cao, tạo n&ecirc;n v&ograve;ng tr&ograve;n gồm kh&iacute; bụi v&agrave; sao xung quanh. NASA m&ocirc; tả hiệu ứng n&agrave;y tương tự gợn s&oacute;ng tạo ra khi n&eacute;m vi&ecirc;n đ&aacute; xuống hồ nước.</p>\r\n\r\n<p>&quot;Những quan s&aacute;t của James Webb nhấn mạnh Cartwheel đang trong giai đoạn tạm thời. Một thi&ecirc;n h&agrave;, c&oacute; lẽ trước đ&acirc;y chỉ thuộc dạng xoắn ốc b&igrave;nh thường như Dải Ng&acirc;n h&agrave; trước vụ va chạm, sẽ tiếp tục biến đổi trong tương lai&quot;, NASA cho biết.</p>\r\n\r\n<p>Cấu tr&uacute;c của Cartwheel được đ&aacute;nh gi&aacute; rất phức tạp với 2 v&ograve;ng, v&ograve;ng ngo&agrave;i l&agrave; đ&aacute;m kh&iacute; để h&igrave;nh th&agrave;nh sao v&agrave; lu&ocirc;n mở rộng, v&ograve;ng b&ecirc;n trong chứa hạt nh&acirc;n, bao quanh c&aacute;c sao gi&agrave;.</p>\r\n\r\n<p>Nhờ c&aacute;c c&ocirc;ng cụ hiện đại, James Webb c&oacute; thể thu thập dữ liệu, gi&uacute;p x&aacute;c định tuổi của c&aacute;c sao trong Cartwheel. K&iacute;nh viễn vọng trị gi&aacute;&nbsp;10 tỷ USD&nbsp;c&ograve;n tiết lộ th&agrave;nh phần lớp bụi v&ograve;ng ngo&agrave;i c&oacute; cả silicat, hợp chất được d&ugrave;ng phổ biến tr&ecirc;n Tr&aacute;i Đất.</p>\r\n\r\n<p>Nghi&ecirc;n cứu của NASA cũng cho thấy Cartwheel l&agrave; nơi h&igrave;nh th&agrave;nh rất nhiều sao mới. &quot;Phần l&otilde;i s&aacute;ng chứa lượng bụi n&oacute;ng cực lớn, c&aacute;c v&ugrave;ng s&aacute;ng nhất l&agrave; nơi tr&uacute; ngụ của những cụm sao trẻ khổng lồ. Mặt kh&aacute;c, v&ograve;ng ngo&agrave;i thi&ecirc;n h&agrave; đ&atilde; mở rộng suốt 440 triệu năm, chủ yếu chứa c&aacute;c si&ecirc;u t&acirc;n tinh (supernova) v&agrave; sự h&igrave;nh th&agrave;nh của sao trẻ&quot;, cơ quan n&agrave;y cho biết.</p>\r\n\r\n<p>H&igrave;nh ảnh Cartwheel từng được chụp bằng Đ&agrave;i quan s&aacute;t tia X Chandra v&agrave; k&iacute;nh viễn vọng Hubble. So với những kết quả trước đ&acirc;y, quan s&aacute;t từ James Webb thu được nhiều dữ liệu hơn, đặc biệt l&agrave; c&aacute;c chi tiết ph&iacute;a sau lớp bụi ngo&agrave;i nhờ c&ocirc;ng cụ quan s&aacute;t hồng ngoại trung (MIRI).</p>\r\n\r\n<p>Cartwheel c&oacute; đường k&iacute;nh khoảng 150.000 năm &aacute;nh s&aacute;ng, thuộc ch&ograve;m sao Sculptor (Ngọc Phu). Theo&nbsp;<em>NewScientist</em>, h&igrave;nh ảnh từ James Webb cũng tiết lộ một v&ugrave;ng h&igrave;nh th&agrave;nh sao nằm b&ecirc;n phải Cartwheel, s&aacute;ng hơn so với t&iacute;nh to&aacute;n của c&aacute;c nh&agrave; khoa học.</p>\r\n\r\n<p>James Webb được ph&oacute;ng v&agrave;o th&aacute;ng 12/2021, hoạt động ch&iacute;nh thức từ th&aacute;ng 7 năm nay sau khi hiệu chỉnh. Đ&acirc;y l&agrave; bản kế nhiệm của k&iacute;nh viễn vọng Hubble, d&ugrave;ng để ph&aacute;t hiện c&aacute;c ng&ocirc;i sao, thi&ecirc;n h&agrave; cổ đại, phục vụ t&igrave;m hiểu lịch sử h&igrave;nh th&agrave;nh của vũ trụ. Những c&ocirc;ng cụ hiện đại gi&uacute;p James Webb thu thập nhiều chi tiết hơn so với Hubble.</p>\r\n\r\n<p>Với những c&ocirc;ng cụ mạnh mẽ, c&aacute;c nh&agrave; khoa học c&ograve;n sử dụng James Webb để ph&acirc;n t&iacute;ch c&aacute;c vật thể, hiện tượng vũ trụ, bao gồm nh&oacute;m h&agrave;nh tinh thuộc Hệ Mặt Trời như Mộc tinh. Từ giữa th&aacute;ng 7, nh&oacute;m nghi&ecirc;n cứu James Webb đ&atilde; chia sẻ loạt ảnh m&agrave;u chi tiết về một số khu vực trong vũ trụ. C&aacute;c nh&agrave; thi&ecirc;n văn học cũng t&igrave;m thấy thi&ecirc;n h&agrave; cổ nhất nhờ k&iacute;nh viễn vọng trị gi&aacute;&nbsp;10 tỷ USD.</p>', 'Từ Quốc Minh', 0, 5, '2208039415701303082022.jpeg', 'publish', '2022-08-03 02:41:57', NULL),
(23, 'Nạp tiền tỷ nâng cấp nhân vật, game thủ không chơi được vì quá mạnh', 'nap-tien-ty-nang-cap-nhan-vat-game-thu-khong-choi-duoc-vi-qua-manh', '<p>Một game thủ đ&atilde; chi 100.000 USD v&agrave;o tựa game Diablo Immortal khiến nh&acirc;n vật qu&aacute; mạnh v&agrave; kh&ocirc;ng thể gh&eacute;p trận.</p>\r\n\r\n<p>Diablo Immortal l&agrave; phần mới nhất trong loạt game nhập vai h&agrave;nh động Diablo nổi tiếng của Blizzard. Game rất được mong đợi, bởi l&agrave; phần tiếp theo sau 10 năm kể từ ng&agrave;y ra mắt Diablo 3.</p>\r\n\r\n<p>T&iacute;nh năng được đ&aacute;nh gi&aacute; cao của game l&agrave; chơi ch&eacute;o, cho ph&eacute;p game thủ chơi tr&ecirc;n cả thiết bị di động v&agrave; PC. Tuy nhi&ecirc;n, Diablo Immortal cũng bị chỉ tr&iacute;ch kh&ocirc;ng &iacute;t về t&iacute;nh chất &ldquo;h&uacute;t m&aacute;u&quot;, khiến người chơi phải bỏ ra số tiền rất cao để n&acirc;ng cấp nh&acirc;n vật.</p>\r\n\r\n<p>Việc nạp qu&aacute; nhiền tiền v&agrave;o game chưa chắc đ&atilde; mang lại lợi &iacute;ch cho người chơi. Một YouTuber c&oacute; t&ecirc;n Jtisallbusiness đ&atilde; nạp hơn&nbsp;100.000 USD&nbsp;v&agrave;o tr&ograve; chơi để tạo ra một nh&acirc;n vật mạnh mẽ đến mức &ldquo;bất bại&rdquo;, chiến thắng tất cả người chơi kh&aacute;c trong game.</p>\r\n\r\n<p>Điều n&agrave;y khiến t&iacute;nh năng gh&eacute;p cặp trong Diablo Immortal kh&ocirc;ng thể t&igrave;m ra đối thủ ph&ugrave; hợp với người chơi tr&ecirc;n, v&igrave; tỉ lệ thắng của anh qu&aacute; cao. Cuối c&ugrave;ng, việc chi tiền v&agrave;o n&acirc;ng cấp nh&acirc;n vật của anh khiến anh kh&ocirc;ng c&ograve;n được chơi game nữa.</p>\r\n\r\n<p>&ldquo;Kh&ocirc;ng hề n&oacute;i qu&aacute; khi t&ocirc;i mất tới khoảng 48-72 tiếng trong h&agrave;ng chờ m&agrave; vẫn kh&ocirc;ng thể t&igrave;m được một trận đấu Battleground&rdquo;, Jtisallbusiness chia sẻ.</p>\r\n\r\n<p>Chưa hết, trong thời điểm n&agrave;y, Jtisallbusiness c&ugrave;ng nh&oacute;m của anh đang tham gia một sự kiện lớn của game với ti&ecirc;u ch&iacute; gia tăng điểm của đội th&ocirc;ng qua c&aacute;c trận PvP Battlegrounds (người chơi chiến đấu với nhau).</p>\r\n\r\n<p>Việc phải đứng ngo&agrave;i cuộc chơi, kh&ocirc;ng thể tham gia chiến đấu v&igrave; nh&acirc;n vật qu&aacute; mạnh sẽ khiến nh&oacute;m của anh gặp rất nhiều bất lợi.</p>\r\n\r\n<p>&ldquo;V&igrave; vậy, mặc d&ugrave; l&agrave; trưởng nh&oacute;m, t&ocirc;i kh&ocirc;ng thể tham gia chiến đấu v&agrave; kiếm điểm cho đội của t&ocirc;i trong sự kiện. Mặc d&ugrave; đ&atilde; li&ecirc;n hệ với Blizzard, nhưng hiện tại th&igrave; t&ocirc;i vẫn lực bất t&ograve;ng t&acirc;m&rdquo;, game thủ tiếp tục.</p>\r\n\r\n<p>Việc game thủ n&agrave;y đang kiếm tiền từ Diablo Immortal với tư c&aacute;ch l&agrave; một streamer v&agrave; người tạo nội dung c&agrave;ng khiến anh thất vọng. Đ&acirc;y l&agrave; một kết cục kh&ocirc;ng ai mong muốn, nhất l&agrave; khi đ&atilde; nạp&nbsp;100.000 USD&nbsp;cho game.</p>\r\n\r\n<p>Cơ chế Pay-to-Win (nạp tiền để tăng sức mạnh) của Diablo Immortal đ&atilde; trở th&agrave;nh chủ đề được b&agrave;n t&aacute;n rất nhiều bởi cộng đồng game thủ thế giới kể từ khi tr&ograve; chơi ra mắt v&agrave;o th&aacute;ng 6 năm nay.</p>\r\n\r\n<p>Theo đ&oacute;, một số người d&ugrave;ng Reddit đ&atilde; chỉ ra rằng người chơi cần nạp khoảng&nbsp;100.000 USD&nbsp;chỉ để n&acirc;ng cấp tối đa một nh&acirc;n vật trong game, v&agrave; Diablo Immortal c&oacute; tới 6 nh&acirc;n vật kh&aacute;c nhau.</p>', 'Từ Quốc Minh', 0, 5, '2208039434186598c487ed53ce31c39af254f6dab7a.png', 'publish', '2022-08-03 02:43:41', NULL);
INSERT INTO `posts` (`pid`, `title`, `slug`, `description`, `author`, `view`, `category_id`, `image`, `status`, `created_at`, `updated_at`) VALUES
(24, 'Cáp quang AAG bị lỗi từ tháng 2 vẫn chưa sửa xong', 'cao-quang-aag-bi-loi-tu-thang-2-van-chua-sua-xong', '<p>Hiện cả tuyến AAG v&agrave; APG đều gặp vấn đề. C&aacute;c lỗi tr&ecirc;n tuyến c&aacute;p AAG từ đầu năm đến nay vẫn chưa được khắc phục.</p>\r\n\r\n<p>Asia Pacific Gateway (APG) v&agrave; Asia America Gateway (AAG) l&agrave; 2 trong 5 tuyến c&aacute;p quang biển quan trọng nhất của Việt Nam, gi&uacute;p kết nối Internet đi quốc tế. Tuy nhi&ecirc;n, đ&acirc;y cũng l&agrave; những tuyến c&aacute;p quang thường xuy&ecirc;n gặp sự cố, khiến lưu lượng đường truyền bị gi&aacute;n đoạn.</p>\r\n\r\n<p>Theo chia sẻ của một nh&agrave; cung cấp dịch vụ Internet (ISP) tại Việt Nam, trung tuần th&aacute;ng 2 v&agrave; cuối th&aacute;ng 6, tuyến c&aacute;p AAG li&ecirc;n tục gặp sự cố tr&ecirc;n c&aacute;c hướng kết nối đi Singapore v&agrave; Hong Kong. Cụ thể, với hướng kết nối Singapore, AAG gặp sự cố tr&ecirc;n nh&aacute;nh S1B, S1D v&agrave; lỗi nguồn tại trạm TungKu (Brunei).</p>\r\n\r\n<p>Với hướng kết nối Hong Kong, AAG gặp lỗi &ldquo;shunt fault&rdquo; (lỗi d&ograve;ng điện) tr&ecirc;n nh&aacute;nh S1H. Nh&agrave; cung cấp tr&ecirc;n cũng cho biết th&ecirc;m c&aacute;c sự cố tr&ecirc;n tuyến AAG hiện vẫn chưa c&oacute; thời gian xử l&yacute; v&agrave; khắc phục cụ thể.</p>\r\n\r\n<p>V&agrave;o chiều 26/7, tuyến c&aacute;p biển APG xảy ra sự cố tr&ecirc;n ph&acirc;n đoạn S3, c&aacute;ch trạm Chongming (Trung Quốc) khoảng 416 km. Do trạm n&agrave;y đang cấp nguồn từ trục ch&iacute;nh đến trục S1.7 n&ecirc;n khi ph&acirc;n đoạn S3 gặp sự cố, t&igrave;nh trạng mất nối kết đ&atilde; diễn ra.</p>\r\n\r\n<p>Điều n&agrave;y khiến to&agrave;n bộ lưu lượng từ Việt Nam kết nối qua trục ch&iacute;nh tr&ecirc;n c&aacute;p APG đến Hong Kong, Singapore, Malaysia v&agrave; Nhật Bản đều bị ảnh hưởng.</p>\r\n\r\n<p>Ngay sau khi nhận th&ocirc;ng tin sự cố, c&aacute;c nh&agrave; mạng đ&atilde; nhanh ch&oacute;ng l&ecirc;n phương &aacute;n định tuyến lại dung lượng. Đồng thời, họ cũng phối hợp với c&aacute;c nh&agrave; thầu để nghi&ecirc;n cứu phương &aacute;n t&aacute;i cấu h&igrave;nh tuyến APG nhằm sớm kh&ocirc;i phục lưu lượng tr&ecirc;n trục ch&iacute;nh.</p>\r\n\r\n<p>Tới ng&agrave;y 28/7, c&aacute;c cơ quan đ&atilde; thống nhất đề xuất của NEC về phương &aacute;n cấu h&igrave;nh lại nguồn. Sau khi khắc phục tạm thời, to&agrave;n bộ lưu lượng kết nối từ Việt Nam đi Hong Kong, Singapore qua trục ch&iacute;nh của tuyến c&aacute;p APG đ&atilde; trở lại b&igrave;nh thường. Tuy nhi&ecirc;n, lưu lượng kết nối đến Nhật Bản v&agrave; Malaysia vẫn chưa được kh&ocirc;i phục.</p>\r\n\r\n<p>Hiện tại, c&aacute;c nh&agrave; mạng tại Việt Nam vẫn chưa nhận được th&ocirc;ng b&aacute;o về kế hoạch sửa chữa, khắc phục ho&agrave;n to&agrave;n sự cố tr&ecirc;n 2 tuyến c&aacute;p biển AAG v&agrave; APG.</p>\r\n\r\n<p>Tuyến c&aacute;p APG c&oacute; chiều d&agrave;i khoảng 10.400 km, được đặt ngầm dưới biển Th&aacute;i B&igrave;nh Dương, với băng th&ocirc;ng tối đa l&ecirc;n tới 54 Tbps. Tuyến c&aacute;p n&agrave;y c&oacute; nhiệm vụ kết nối giữa Việt Nam, Trung Quốc, Hong Kong, Đ&agrave;i Loan, Nhật Bản, H&agrave;n Quốc, Malaysia, Singapore v&agrave; Th&aacute;i Lan. Tuyến APG c&oacute; sự tham gia đầu tư của Việt Nam v&agrave; bắt đầu khai th&aacute;c từ th&aacute;ng 12/2016.</p>\r\n\r\n<p>Trong khi đ&oacute;, AAG l&agrave; tuyến c&aacute;p biển được đưa v&agrave;o vận h&agrave;nh c&aacute;ch đ&acirc;y 12 năm, với tổng chiều d&agrave;i 20.000 km v&agrave; dung lượng tối đa 2 Tbps, kết nối trực tiếp khu vực Đ&ocirc;ng Nam &Aacute; với Mỹ. Tuyến c&aacute;p bắt đầu từ Malaysia đến điểm cuối tại Mỹ. Nh&aacute;nh c&aacute;p thuộc Việt Nam nằm trong đoạn S1, c&oacute; chiều d&agrave;i 314 km.</p>\r\n\r\n<p>Kể từ khi được đưa v&agrave;o khai th&aacute;c, AAG đ&atilde; nhiều lần gặp sự cố. Tuy nhi&ecirc;n, tuyến c&aacute;p n&agrave;y hiện vẫn được nhiều nh&agrave; mạng trong nước sử dụng, do c&oacute; gi&aacute; th&agrave;nh hợp l&yacute; nhất.</p>', 'Từ Quốc Minh', 3, 5, '22080394429anh_1_1.jpg', 'publish', '2022-08-03 02:44:29', NULL),
(25, 'Mảnh vỡ tên lửa Trung Quốc rơi xuống Đông Nam Á', 'manh-vo-ten-lua-tq-roi-xuong-dna', '<p>Một số mảnh vỡ của t&ecirc;n lửa Long March 5B được t&igrave;m thấy tại Indonesia v&agrave; Malaysia sau khi rơi kh&ocirc;ng kiểm so&aacute;t xuống Tr&aacute;i Đất.</p>\r\n\r\n<p>Nh&agrave; vật l&yacute; thi&ecirc;n văn Jonathan McDowell thuộc Trung t&acirc;m Vật l&yacute; Thi&ecirc;n văn Harvard - Smithsonian cho biết một số mảnh vỡ t&ecirc;n lửa Long March 5B (Trường Chinh 5B) của Trung Quốc được t&igrave;m thấy tại Kalimantan (Indonesia) v&agrave; Sarawak (Malaysia).</p>\r\n\r\n<p>Theo McDowell, v&agrave;i mảnh vỡ được t&igrave;m thấy bởi người d&acirc;n v&ugrave;ng Sanggau, nằm gần bi&ecirc;n giới Indonesia v&agrave; Malaysia, kh&ocirc;ng ghi nhận thương vong về người hoặc thiệt hại t&agrave;i sản. Tuy nhi&ecirc;n với k&iacute;ch thước kh&aacute; lớn, ch&uacute;ng sẽ g&acirc;y nguy hiểm nếu rơi xuống khu d&acirc;n cư.</p>\r\n\r\n<p>Phần l&otilde;i nặng gần 23 tấn của Long March 5B đ&atilde; rơi kh&ocirc;ng kiểm so&aacute;t xuống Ấn Độ Dương v&agrave;o 30/7. Theo t&iacute;nh to&aacute;n, phần lớn t&ecirc;n lửa bị thi&ecirc;u rụi khi qua bầu kh&iacute; quyển, tuy nhi&ecirc;n khoảng 20-40% trọng lượng c&ograve;n lại, vốn được thiết kế chịu nhiệt tốt hơn c&oacute; thể rơi trở lại Tr&aacute;i Đất.</p>\r\n\r\n<p>Long March 5B được ph&oacute;ng v&agrave;o ng&agrave;y 24/7. T&ecirc;n lửa n&agrave;y đến trạm vũ trụ Thi&ecirc;n H&agrave; (Tiangong) của Trung Quốc v&agrave;o 25/7, mang theo một module phục vụ c&aacute;c th&iacute; nghiệm khoa học. Phần l&otilde;i t&ecirc;n lửa đi đến quỹ đạo, sau đ&oacute; trở lại Tr&aacute;i Đất bằng lực cản kh&iacute; quyển trong 6 ng&agrave;y tiếp theo.</p>\r\n\r\n<p>Do để rơi tự do, Long March 5B tạo ra nhiều lo ngại về khả năng g&acirc;y thương vong nếu kh&ocirc;ng may rơi xuống khu d&acirc;n cư. Theo&nbsp;<em>Space</em>, cộng đồng khoa học đ&atilde; chỉ tr&iacute;ch cơ quan vũ trụ của Trung Quốc do để l&otilde;i của Long March 5B rơi tự do trong cả 3 nhiệm vụ.</p>\r\n\r\n<p>Sứ mệnh đầu ti&ecirc;n của Long March 5B diễn ra v&agrave;o th&aacute;ng 5/2020. Sau khi ho&agrave;n tất, một số mảnh vỡ t&ecirc;n lửa rơi xuống Bờ Biển Ng&agrave;. Trong nhiệm vụ thứ 2 v&agrave;o th&aacute;ng 4/2021, t&agrave;n t&iacute;ch của t&ecirc;n lửa đ&atilde; rơi xuống Ấn Độ Dương. Trung Quốc dự t&iacute;nh d&ugrave;ng Long March 5B để gửi một module kh&aacute;c l&ecirc;n Tiangong v&agrave;o th&aacute;ng 10 năm nay.</p>\r\n\r\n<p>&quot;N&ecirc;n để lại một lượng nhi&ecirc;n liệu gi&uacute;p t&ecirc;n lửa trở lại một c&aacute;ch c&oacute; kiểm so&aacute;t&quot;, Darren McKnight, chuy&ecirc;n gia kỹ thuật của c&ocirc;ng ty theo d&otilde;i vệ tinh LeoLabs tại California cho biết.</p>\r\n\r\n<p>&ldquo;Tất cả quốc gia với sứ mệnh du h&agrave;nh vũ trụ n&ecirc;n tu&acirc;n theo c&aacute;c phương ph&aacute;p tốt nhất đ&atilde; được thiết lập. Đồng thời, họ n&ecirc;n chia sẻ trước th&ocirc;ng tin để dự đo&aacute;n về nguy cơ t&aacute;c động của mảnh vỡ, đặc biệt đối với c&aacute;c phương tiện vận tải hạng nặng như Long March 5B&rdquo;, &ocirc;ng Bill Nelson, Gi&aacute;m đốc Cơ quan H&agrave;ng kh&ocirc;ng Vũ trụ Mỹ (NASA) cho biết tr&ecirc;n Twitter.</p>', 'Từ Quốc Minh', 2, 5, '2208039451500402082022.jpg', 'publish', '2022-08-03 02:45:15', NULL),
(26, 'Elon Musk sắp xây sân bay riêng', 'elon-musk-sap-xay-san-bay-rieng', '<p>S&acirc;n bay của vị tỷ ph&uacute; được cho đặt tại Texas, phục vụ c&aacute;c c&ocirc;ng ty đang đ&oacute;ng qu&acirc;n như Tesla, SpaceX v&agrave; Boring Company.</p>\r\n\r\n<p>Trang&nbsp;<em>Austonia</em>&nbsp;đưa tin Elon Musk c&oacute; kế hoạch x&acirc;y dựng s&acirc;n bay ri&ecirc;ng tại bang Texas. Dự &aacute;n được cho sẽ nằm ở ph&iacute;a đ&ocirc;ng th&agrave;nh phố Austin, gần hạt Bastrop. D&ugrave; vậy, địa điểm v&agrave; thời gian khởi c&ocirc;ng cụ thể chưa được tiết lộ.</p>\r\n\r\n<p>Một số c&ocirc;ng ty do Musk s&aacute;ng lập như SpaceX v&agrave; Boring Company đều đặt trụ sở tại Texas. Cuối năm 2021, vị tỷ ph&uacute; cũng tuy&ecirc;n bố chuyển trụ sở h&atilde;ng xe điện Tesla từ California sang Texas. Do đ&oacute;, s&acirc;n bay ri&ecirc;ng tại đ&acirc;y c&oacute; thể vận chuyển người, h&agrave;ng h&oacute;a để phục vụ hoạt động tại c&aacute;c cơ sở sản xuất.</p>\r\n\r\n<p>Theo&nbsp;<em>Business Insider</em>, Musk v&agrave; một số c&ocirc;ng ty của &ocirc;ng đang sở hữu nhiều mảnh đất tại Texas, bao gồm khu&ocirc;n vi&ecirc;n rộng 850 ha cho nh&agrave; m&aacute;y Giga Texas của Tesla tại ph&iacute;a đ&ocirc;ng nam hạt Travis. Đ&acirc;y c&ograve;n l&agrave; trụ sở ch&iacute;nh của Tesla.</p>\r\n\r\n<p>SpaceX v&agrave; Boring Company cũng đứng t&ecirc;n nhiều mảnh đất tại trung t&acirc;m Texas, đa số dọc theo h&agrave;nh lang s&ocirc;ng Colorado. Trong đ&oacute;, Boring Company vừa chuyển trụ sở đến th&agrave;nh phố Pflugerville, ph&iacute;a đ&ocirc;ng bắc Austin, c&aacute;ch s&acirc;n bay Austin Executive khoảng 4,8 km.</p>\r\n\r\n<p>Năm 2021, Gapped Bass LLC, tổ chức li&ecirc;n kết với c&aacute;c l&atilde;nh đạo của Boring Company đ&atilde; mua mảnh đất khoảng 30 ha tại ph&iacute;a t&acirc;y bắc Bastrop. Th&aacute;ng 2 năm nay, đại diện c&ocirc;ng ty đ&atilde; đạt thỏa thuận với ch&iacute;nh quyền hạt Bastrop để x&acirc;y dựng tổ hợp sản xuất rộng khoảng 0,74 ha.</p>\r\n\r\n<p>Hiện chưa r&otilde; diện t&iacute;ch s&acirc;n bay ri&ecirc;ng của Musk. Gần đ&oacute;, s&acirc;n bay Austin Executive rộng khoảng 240 ha. Tại lễ kh&aacute;nh th&agrave;nh nh&agrave; m&aacute;y Giga Texas hồi th&aacute;ng 4, Musk cho biết địa điểm n&agrave;y c&aacute;ch s&acirc;n bay quốc tế Austin-Bergstrom khoảng 10 ph&uacute;t l&aacute;i xe.</p>\r\n\r\n<p>Musk thường di chuyển bằng m&aacute;y bay ri&ecirc;ng (Gulfstream G650ER) đến c&aacute;c nh&agrave; m&aacute;y của Tesla. Cuối th&aacute;ng 6,&nbsp;<em>Austonia</em>&nbsp;đưa tin tỷ ph&uacute; gi&agrave;u nhất thế giới c&oacute; kế hoạch n&acirc;ng cấp l&ecirc;n mẫu G700 mới hơn, dự kiến giao v&agrave;o năm 2023 với gi&aacute;&nbsp;78 triệu USD.</p>\r\n\r\n<p>Việc x&acirc;y dựng s&acirc;n bay tư nh&acirc;n cần được Cục H&agrave;ng kh&ocirc;ng Li&ecirc;n bang Mỹ (FAA) cấp giấy ph&eacute;p, quan trọng nhất l&agrave; đ&aacute;p ứng ti&ecirc;u chuẩn về m&ocirc;i trường do cơ quan n&agrave;y đề ra. Musk cũng cần l&agrave;m việc với ch&iacute;nh quyền Texas để x&acirc;y s&acirc;n bay tại tiểu bang n&agrave;y.</p>', 'Từ Quốc Minh', 4, 5, '2208039461800201082022.jpeg', 'publish', '2022-08-03 02:46:18', NULL);

